        cube([phone_width, phone_depth, phone_height], center=true);

        // Inner cavity
        translate([0, 0, wall_thickness/2])
        cube([phone_width-wall_thickness*2, phone_depth-wall_thickness*2, phone_height-wall_thickness], center=true);

