        translate([-phone_width/2+wall_thickness, phone_depth/2-wall_thickness, phone_height/2-wall_thickness])
        sphere(r=wall_thickness*2, $fn=16);
        translate([phone_width/2-wall_thickness, -phone_depth/2+wall_thickness, phone_height/2-wall_thickness])
        sphere(r=wall_thickness*2, $fn=16);
        translate([-phone_width/2+wall_thickness, -phone_depth/2+wall_thickness, phone_height/2-wall_thickness])
        sphere(r=wall_thickness*2, $fn=16);
