// 50 mm diameter piezo buzzer with PCB mount, realistic style
// Wall thickness 1.5 mm for 3D printing, no overhangs

$fn = 64;

// Overall scale factor â€“ 50 mm diameter
scale_factor = 1;

// Main body
module buzzer_body() {
    color([0.1, 0.1, 0.1]) // Matte black plastic
    difference() {
        cylinder(h = 15, d = 50, center = true);
        // Central hole for sound
        cylinder(h = 16, d = 25, center = true);
    }
}

// Piezo diaphragm visible from top
module piezo_diaphragm() {
    color([0.9, 0.9, 0.9]) // Silver metallic
    translate([0, 0, 7.4])
    difference() {
        cylinder(h = 0.2, d = 28);
        cylinder(h = 0.3, d = 8);
    }
}

// Vent holes around the top edge for sound
module vent_holes() {
    for (i = [0:11]) {
        rotate([0, 0, i * 30])
        translate([20, 0, 7])
        color([0.1, 0.1, 0.1])
        cylinder(h = 2, d = 4);
    }
}

// PCB base plate
module pcb_base() {
    color([0.0, 0.7, 0.0]) // Green soldermask
    difference() {
        cylinder(h = 2, d = 50, center = true);

        // Mounting holes
        for (i = [0:3]) {
            rotate([0, 0, i * 90 + 45])
            translate([18, 0, 0])
            cylinder(h = 3, d = 3.2, center = true);
        }

        // Center hole
        cylinder(h = 3, d = 25, center = true);
    }
}

// Copper pads on PCB
module copper_pads() {
    color([0.8, 0.4, 0.0]) // Copper color
    for (i = [0:1]) {
        rotate([0, 0, i * 180])
        translate([15, 0, 0])
        cube([8, 5, 0.2], center = true);
    }
}

// Solder points for wires
module solder_points() {
    color([0.6, 0.6, 0.6]) // Silver solder
    for (i = [0:1]) {
        rotate([0, 0, i * 180])
        translate([15, 0, 1.1])
        sphere(d = 2.5);
    }
}

// Indicator label area
module label_area() {
    color([0.95, 0.95, 0.95]) // White label
    translate([0, 0, -7.5])
    difference() {
        cube([20, 8, 0.5], center = true);
        translate([0, 0, 0.3])
        color([0, 0, 0])
        linear_extrude(0.3)
        text("12V BUZZER", size = 3, halign = "center", valign = "center");
    }
}

// Main assembly
union() {
    buzzer_body();
    piezo_diaphragm();
    vent_holes();
    translate([0, 0, -8.5]) pcb_base();
    translate([0, 0, -8.4]) copper_pads();
    translate([0, 0, -8.4]) solder_points();
    label_area();
}

// Vibrator with realistic styling - medium complexity
// Designed for 3D printing with proper wall thickness

// Main parameters
$fn = 50;
wall_thickness = 2;
main_diameter = 35;
main_length = 85;
taper_length = 15;
button_diameter = 6;
button_height = 2;

// Main body - tapered cylinder
module main_body() {
    color("#FFB6C1")  // Light pink silicone
    difference() {
        // Main tapered shape
        cylinder(h = main_length, d1 = main_diameter, d2 = main_diameter * 0.8);

        // Hollow center
        translate([0, 0, wall_thickness])
        cylinder(h = main_length - wall_thickness * 2, d1 = main_diameter - wall_thickness * 2, d2 = (main_diameter * 0.8) - wall_thickness * 2);

        // Charging port hole
        translate([0, -main_diameter/2 + 1, main_length - 15])
        rotate([90, 0, 0])
        cylinder(h = main_diameter/4, d = 8);
    }
}

// Control buttons
module control_panel() {
    color("#FF1493")  // Deep pink
    translate([0, main_diameter/2 + 0.1, main_length - 25])
    difference() {
        // Panel base
        cube([25, 1, 20], center = true);

        // Button holes
        for(i = [-1, 0, 1]) {
            translate([i * 8, 0, 0])
            cylinder(h = 2, d = button_diameter + 1);
        }
    }

    // Individual buttons
    for(i = [-1, 0, 1]) {
        color("#FFFFFF")
        translate([i * 8, main_diameter/2 + 1.1, main_length - 25])
        cylinder(h = button_height, d = button_diameter);
    }
}

// LED indicator ring
module led_ring() {
    color("#FF0000", 0.5)  // Translucent red
    translate([0, 0, main_length - 30])
    difference() {
        cylinder(h = 2, d = main_diameter + 2);
        cylinder(h = 3, d = main_diameter - 4);
    }
}

// Textured grip pattern
module grip_pattern() {
    color("#FF69B4")  // Hot pink
    for(i = [0:15]) {
        rotate([0, 0, i * 24])
        translate([main_diameter/2 - 1, 0, 15])
        rotate([90, 0, 0])
        cylinder(h = main_length - 30, d = 3);
    }
}

// Charging contacts
module charging_contacts() {
    color("#C0C0C0")  // Silver
    translate([0, -main_diameter/2 + 2, main_length - 12])
    cube([12, 1, 4], center = true);

    translate([0, -main_diameter/2 + 2.5, main_length - 12])
    cube([8, 0.5, 2], center = true);
}

// Base cap with threads
module base_cap() {
    color("#FFB6C1")
    translate([0, 0, -5])
    difference() {
        cylinder(h = 10, d = main_diameter);

        // Thread pattern
        for(i = [0:3]) {
            rotate([0, 0, i * 90])
            translate([main_diameter/2 - 2, 0, 0])
            cube([4, 1, 10]);
        }

        // Center hole for charging
        cylinder(h = 11, d = 8);
    }
}

// Assembly
main_body();
control_panel();
led_ring();
grip_pattern();
charging_contacts();
base_cap();

