// Wi-Fi Router / Signal Booster
// Realistic style, medium complexity

$fn = 32;

// Main colors
color_body = [0.15, 0.15, 0.15];
color_antenna = [0.2, 0.2, 0.2];
color_led = [0, 1, 0];
color_port = [0.8, 0.8, 0.8];
color_label = [0.9, 0.9, 0.9];

// Main router body
module router_body() {
    color(color_body)
    difference() {
        // Main housing
        cube([120, 80, 25], center = true);

        // Ventilation slots on top
        for (x = [-50:15:50]) {
            translate([x, 0, 12.6])
                cube([8, 75, 2]);
        }

        // Front LED indicator strip
        translate([0, 42, 0])
            cube([60, 2, 5], center = true);
    }

    // Side ventilation holes
    for (y = [-30:8:30]) {
        color(color_body)
        translate([61, y, 0])
            rotate([0, 90, 0])
                cylinder(h = 2, r = 2);
        translate([-61, y, 0])
            rotate([0, 90, 0])
                cylinder(h = 2, r = 2);
    }
}

// Antenna module
module antenna(rotate_base = 0, rotate_tilt = 0) {
    color(color_antenna)
    translate([0, 0, 12.5])
    rotate([0, 0, rotate_base])
    translate([0, 45, 0])
    rotate([rotate_tilt, 0, 0])
    translate([0, 0, 25])
    union() {
        // Antenna base
        cylinder(h = 8, r = 6);

        // Antenna rod
        translate([0, 0, 4])
            cylinder(h = 50, r = 2);

        // Antenna tip
        translate([0, 0, 54])
            sphere(r = 3);
    }
}

// Ethernet port
module ethernet_port(x_pos) {
    color(color_port)
    translate([x_pos, -42, 0])
    union() {
        // Port housing
        difference() {
            cube([12, 8, 8], center = true);
            translate([0, -4.1, 0])
                cube([10, 8, 6], center = true);
        }

        // Port pins (simplified)
        for (i = [-3:1:3]) {
            translate([i * 1.2, -4.5, 2])
                cube([0.5, 1, 0.5]);
        }
    }
}

// LED indicators
module led_indicators() {
    // Power LED
    color(color_led)
    translate([-25, 42.5, 0])
        cylinder(h = 1, r = 2);

    // Signal strength LEDs
    for (i = [0:3]) {
        color([0, 0.8 - i * 0.2, 0])
        translate([10 + i * 8, 42.5, 0])
            cylinder(h = 1, r = 1.5);
    }
}

// Brand label
module brand_label() {
    color(color_label)
    translate([0, 20, -12.6])
    linear_extrude(height = 0.5)
    text("SIGNAL+", size = 8, halign = "center");
}

// Power connector
module power_connector() {
    color(color_port)
    translate([50, -42, 0])
    union() {
        // Connector housing
        cube([8, 12, 8], center = true);

        // Center pin
        translate([0, -6.5, 0])
            cylinder(h = 3, r = 1.5);
    }
}

// Assemble the router
union() {
    router_body();

    // Four antennas
    antenna(45, 30);
    antenna(135, 30);
    antenna(225, 30);
    antenna(315, 30);

    // Four ethernet ports
    ethernet_port(-40);
    ethernet_port(-20);
    ethernet_port(0);
    ethernet_port(20);

    // LED indicators
    led_indicators();

    // Brand label
    brand_label();

    // Power connector
    power_connector();
}

// OpenSCAD Digital Voice Recorder (Medium Complexity, Realistic Style)
