// 3D Printable design with proper wall thickness and no overhangs
// Features: Driver cone, surround, basket, magnet, terminals, grill, enclosure

// Define dimensions (mm)
enclosure_d = 80;
enclosure_h = 45;
wall_thickness = 2;
driver_d = 60;
magnet_d = 35;
magnet_h = 15;
cone_depth = 12;
surround_thickness = 2;
grill_thickness = 1.5;
terminal_size = 4;

// Main assembly
union() {
    // Enclosure box
    color([0.1, 0.1, 0.1])  // Dark gray/black
    difference() {
        // Main box
        cube([enclosure_d, enclosure_d, enclosure_h], center=true);

        // Hollow interior
        translate([0, 0, wall_thickness/2])
            cube([enclosure_d-wall_thickness*2, enclosure_d-wall_thickness*2, enclosure_h-wall_thickness], center=true);

        // Front hole for driver
        translate([0, 0, enclosure_h/2])
            cylinder(d=driver_d+4, h=wall_thickness+1, center=true);
    }

    // Front grill
    color([0.2, 0.2, 0.2])
    translate([0, 0, enclosure_h/2+wall_thickness/2])
        grill();

    // Driver assembly
    translate([0, 0, enclosure_h/2-wall_thickness/2])
        driver_assembly();

    // Rear panel features
    color([0.15, 0.15, 0.15])
    translate([enclosure_d/2-wall_thickness/2, 0, 0])
    rotate([0, 90, 0])
        rear_panel();
}

// Driver assembly module
module driver_assembly() {
    // Basket frame
    color([0.4, 0.4, 0.4])  // Light gray
    difference() {
        cylinder(d=driver_d+8, h=8, center=true);
        cylinder(d=driver_d+4, h=9, center=true);
    }

    // Spider (suspension)
    color([0.8, 0.8, 0.8])  // Light gray
    translate([0, 0, -3])
        cylinder(d=driver_d-10, h=1, center=true);

    // Voice coil former
    color([0.3, 0.3, 0.3])
    translate([0, 0, -cone_depth/2])
        cylinder(d=driver_d-25, h=cone_depth+2, center=true);

    // Cone
    color([0.9, 0.9, 0.9])  // White/light gray
    translate([0, 0, -cone_depth/2])
    cylinder(d1=driver_d-4, d2=driver_d-25, h=cone_depth, center=true);

    // Surround (rubber edge)
    color([0.2, 0.2, 0.2])  // Black rubber
    translate([0, 0, -cone_depth/2+cone_depth+1])
    difference() {
        cylinder(d=driver_d, h=2, center=true);
        cylinder(d=driver_d-4, h=3, center=true);
    }

    // Dust cap
    color([0.7, 0.7, 0.7])
    translate([0, 0, -cone_depth/2+cone_depth-1])
        sphere(d=driver_d/4);

    // Magnet
    color([0.3, 0.3, 0.3])  // Dark gray
    translate([0, 0, -10])
        cylinder(d=magnet_d, h=magnet_h, center=true);
}

// Grill module with holes
module grill() {
    difference() {
        // Grill plate
        cylinder(d=driver_d+6, h=grill_thickness, center=true);

        // Center hole
        cylinder(d=driver_d/6, h=grill_thickness+1, center=true);

        // Perimeter holes in circular pattern
        for(i=[0:11]) {
            rotate([0, 0, i*30])
            translate([driver_d/2.5, 0, 0])
                cylinder(d=driver_d/20, h=grill_thickness+1, center=true);
        }

        // Additional holes
        for(i=[0:5]) {
            rotate([0, 0, i*60])
            translate([driver_d/3.5, 0, 0])
                cylinder(d=driver_d/15, h=grill_thickness+1, center=true);
        }
    }
}

// Rear panel with terminals and port
module rear_panel() {
    // Terminal plate
    difference() {
        cube([enclosure_h-4, 20, wall_thickness], center=true);

        // Terminal holes
        for(i=[-1:1]) {
            translate([i*6, 0, 0])
                cylinder(d=terminal_size, h=wall_thickness+1, center=true);
        }
    }

    // Bass reflex port
    color([0.1, 0.1, 0.1])
    translate([10, 0, 0])
    difference() {
        cylinder(d=12, h=wall_thickness+1, center=true);
        cylinder(d=8, h=wall_thickness+2, center=true);
    }

    // Label area
    color([0.05, 0.05, 0.05])
    translate([-5, 0, 0])
        cube([8, 15, 0.5], center=true);
}

