// Module for rounded rectangle
module rounded_cube(size, radius) {
    hull() {
        translate([radius, radius, 0])
            cylinder(h=size.z, r=radius);
        translate([size.x - radius, radius, 0])
            cylinder(h=size.z, r=radius);
        translate([radius, size.y - radius, 0])
            cylinder(h=size.z, r=radius);
        translate([size.x - radius, size.y - radius, 0])
            cylinder(h=size.z, r=radius);
    }
}

// Main enclosure
module enclosure() {
    difference() {
        // Outer shell
        color("darkslategray")
        rounded_cube([case_width, case_depth, case_height], 5);

        // Inner cavity
        translate([wall_thickness, wall_thickness, wall_thickness])
            rounded_cube([
                case_width - 2*wall_thickness,
                case_depth - 2*wall_thickness,
                case_height - wall_thickness
            ], 3);
    }
}

// Front panel
module front_panel() {
    color("black")
    translate([0, 0, case_height - panel_thickness])
        cube([case_width, case_depth, panel_thickness], center=false);
}

// Knob module
module knob(x_pos, label) {
    translate([x_pos, 10, case_height + knob_height/2])
        rotate([90, 0, 0])
        difference() {
            // Knob body
            color("silver")
            cylinder(h=knob_height, r=knob_diameter/2);

            // Indicator line
            translate([0, 0, -0.1])
                cube([knob_diameter/4, 1, knob_height + 0.2], center=true);
        }
}

// Button module
module button(x_pos, y_pos) {
    translate([x_pos, y_pos, case_height + button_diameter/2])
        color("red")
        sphere(d=button_diameter);
}

// RCA connector module
module rca_connector(x_pos, label) {
    translate([x_pos, 5, case_height/2])
        difference() {
            // Outer shell
            color("gold")
            cylinder(h=8, r=4);

            // Inner hole
            color("black")
            cylinder(h=9, r=1.5);
        }
}

// LED indicator
module led_indicator(x_pos) {
    translate([x_pos, case_depth - 10, case_height + 1])
        color("lime")
        sphere(d=3);
}

