device_width = 85;
device_height = 50;
device_depth = 12;
wall_thickness = 2;

// Colors
color_case = [0.15, 0.15, 0.15];        // Dark gray
color_screen = [0.1, 0.1, 0.1];        // Black
color_buttons = [0.8, 0.8, 0.8];       // Light gray
color_ports = [0.3, 0.3, 0.3];         // Medium gray
color_led = [0, 1, 0];                 // Green

// Main case shell
module case_shell() {
    difference() {
        // Outer shell
        color(color_case)
        minkowski() {
            cube([device_width, device_height, device_depth], center = true);
            sphere(wall_thickness);
        }

        // Inner cavity
        translate([0, 0, -1])
        cube([device_width - 4*wall_thickness, 
              device_height - 4*wall_thickness, 
              device_depth - 2*wall_thickness], center = true);
    }
}

// Front face with screen and buttons
module front_panel() {
    // Front plate
    color(color_case)
    translate([0, 0, device_depth/2 - 0.5])
    cube([device_width - 4*wall_thickness, 
          device_height - 4*wall_thickness, 
          1], center = true);

    // Screen recess
    color(color_screen)
    translate([0, 8, device_depth/2])
    cube([device_width - 12*wall_thickness, 
          25, 
          0.5], center = true);

    // Screen glass
    color([0.2, 0.2, 0.2, 0.8])
    translate([0, 8, device_depth/2 + 0.25])
    cube([device_width - 12.5*wall_thickness, 
          24.5, 
          0.1], center = true);
}

// Control buttons
module control_buttons() {
    // Power button
    color(color_buttons)
    translate([device_width/2 - 10, -device_height/2 + 10, device_depth/2])
    rotate([90, 0, 0])
    cylinder(h = 2, r = 3, $fn = 20);

    // Volume buttons
    for(i = [0:1]) {
        translate([device_width/2 - 10, -device_height/2 + 18 + i*6, device_depth/2])
        rotate([90, 0, 0])
        cylinder(h = 1.5, r = 2, $fn = 16);
    }

    // Navigation buttons
    color(color_buttons)
    translate([0, -device_height/2 + 10, device_depth/2])
    rotate([90, 0, 0])
    cylinder(h = 1.5, r = 4, $fn = 20);

    // Center button
    color(color_buttons)
    translate([0, -device_height/2 + 10, device_depth/2 + 1])
    sphere(r = 2, $fn = 16);
}

// LED indicators
module status_leds() {
    for(i = [0:2]) {
        color(color_led)
        translate([-device_width/2 + 10 + i*8, device_height/2 - 5, device_depth/2])
        cylinder(h = 0.5, r = 1.5, $fn = 12);
    }
}

// Ports on the side
module ports() {
    // HDMI port
    color(color_ports)
    translate([device_width/2 - 5, 0, 0])
    rotate([0, 90, 0])
    difference() {
        cube([8, 15, 5], center = true);
        cube([6, 13, 7], center = true);
    }

    // USB-C port
    color(color_ports)
    translate([device_width/2 - 5, 10, 0])
    rotate([0, 90, 0])
    cube([4, 8, 2], center = true);

    // Micro SD slot
    color(color_ports)
    translate([device_width/2 - 5, -10, 0])
    rotate([0, 90, 0])
    cube([2, 12, 1], center = true);
}

// Ventilation holes
module ventilation() {
    color(color_case)
    difference() {
        translate([0, 0, 0])
        cube([device_width, device_height, device_depth], center = true);

        // Top vents
        for(i = [-3:3]) {
            translate([i*10, device_height/2 - 2, 0])
            cube([2, 1, device_depth - 2*wall_thickness]);
        }

        // Bottom vents
        for(i = [-3:3]) {
            translate([i*10, -device_height/2 + 2, 0])
            cube([2, 1, device_depth - 2*wall_thickness]);
        }
    }
}

// Rubber feet
module rubber_feet() {
    color([0.1, 0.1, 0.1])
    for(x = [-1:1]) {
        for(y = [-1:1]) {
            translate([x*30, y*15, -device_depth/2 + 1])
            cylinder(h = 1, r = 3, $fn = 16);
        }
    }
}

// Brand logo placeholder
module logo() {
    color([0.9, 0.9, 0.9])
    translate([0, 0, device_depth/2 + 0.1])
    linear_extrude(height = 0.2)
    text("STREAM", size = 5, halign = "center", valign = "center");
}

// Assembly
union() {
    case_shell();
    front_panel();
    control_buttons();
    status_leds();
    ports();
    ventilation();
    rubber_feet();
    logo();
}

// Realistic Style Switcher Electronic Device
// Medium complexity OpenSCAD model

// Main dimensions
switcher_width = 80;
switcher_height = 30;
switcher_depth = 60;
wall_thickness = 2.5;

// Colors
color_case = [0.15, 0.15, 0.15];  // Dark gray
color_face = [0.2, 0.2, 0.2];     // Slightly lighter
color_ports = [0.1, 0.1, 0.1];    // Black
color_leds = [0, 1, 0];           // Green
color_buttons = [0.8, 0.8, 0.8];  // Light gray
color_screen = [0.05, 0.05, 0.05]; // Very dark

// Main body
module body() {
    difference() {
        // Main case
        color(color_case)
        rounded_box(switcher_width, switcher_height, switcher_depth, 3);

        // Hollow inside for electronics
        translate([0, 0, wall_thickness])
        rounded_box(switcher_width - 2*wall_thickness, 
                   switcher_height - wall_thickness, 
                   switcher_depth - 2*wall_thickness, 2);
    }
}

// Rounded box helper
module rounded_box(w, h, d, r) {
    hull() {
        for (x = [-1, 1])
        for (y = [-1, 1])
        for (z = [-1, 1])
        translate([x*(w/2-r), y*(h/2-r), z*(d/2-r)])
        sphere(r);
    }
}

// Front panel with ports and indicators
module front_panel() {
    translate([0, 0, switcher_depth/2 - 0.1])
    difference() {
        color(color_face)
        cube([switcher_width, switcher_height, 0.5], center=true);

        // Ethernet ports (8P8C)
        for (i = [0, 1, 2, 3]) {
            translate([30 - i*15, -5, 0])
            cube([12, 8, 1], center=true);
        }

        // Status LEDs
        for (i = [0, 1, 2, 3]) {
            translate([30 - i*15, 8, 0])
            cylinder(h=1.5, r=1.5, center=true);
        }
    }

    // Ethernet port details
    for (i = [0, 1, 2, 3]) {
        color(color_ports)
        translate([30 - i*15, -5, switcher_depth/2 + 0.2])
        difference() {
            cube([12, 8, 2], center=true);
            translate([0, 0, 0.5])
            cube([10, 6, 3], center=true);
        }
    }

    // LED indicators
    for (i = [0, 1, 2, 3]) {
        color(color_leds)
        translate([30 - i*15, 8, switcher_depth/2 + 0.8])
        sphere(1.5);
    }
}

// Top panel with screen and buttons
module top_panel() {
    translate([0, switcher_height/2 - 0.1, 0])
    difference() {
        color(color_face)
        cube([switcher_width, 0.5, switcher_depth], center=true);

        // LCD screen cutout
        translate([0, 0, 10])
        cube([40, 1, 20], center=true);

        // Button holes
        translate([-25, 0, -15])
        cylinder(h=1, r=3, center=true);
        translate([-25, 0, -25])
        cylinder(h=1, r=3, center=true);
    }

    // LCD screen
    translate([0, switcher_height/2 + 0.4, 10])
    color(color_screen)
    cube([38, 0.5, 18], center=true);

    // Buttons
    color(color_buttons)
    translate([-25, switcher_height/2 + 0.5, -15])
    cylinder(h=2, r=2.8);
    translate([-25, switcher_height/2 + 0.5, -25])
    cylinder(h=2, r=2.8);

    // Button labels
    translate([-25, switcher_height/2 + 1, -15])
    rotate([90, 0, 0])
    linear_extrude(0.1)
    text("â–²", size=3, halign="center");
    translate([-25, switcher_height/2 + 1, -25])
    rotate([90, 0, 0])
    linear_extrude(0.1)
    text("â–¼", size=3, halign="center");
}

// Side vents for cooling
module side_vents() {
    // Left side
    translate([-switcher_width/2 - 0.1, 0, 0])
    for (i = [0:5]) {
        translate([0, -switcher_height/2 + 5 + i*4, 0])
        cube([0.5, 2, switcher_depth - 10], center=true);
    }

    // Right side
    translate([switcher_width/2 + 0.1, 0, 0])
    for (i = [0:5]) {
        translate([0, -switcher_height/2 + 5 + i*4, 0])
        cube([0.5, 2, switcher_depth - 10], center=true);
    }
}

// Bottom with feet and label
module bottom() {
    translate([0, -switcher_height/2 - 0.1, 0])
    difference() {
        color(color_face)
        cube([switcher_width, 0.5, switcher_depth], center=true);

        // Serial number label area
        translate([0, 0, 0])
        cube([30, 1, 15], center=true);
    }

    // Rubber feet
    color([0.1, 0.1, 0.1])
    for (x = [-1, 1])
    for (z = [-1, 1]) {
        translate([x*30, -switcher_height/2 - 1, z*20])
        cylinder(h=2, r=3);
    }

    // Label
    translate([0, -switcher_height/2 - 0.5, 0])
    rotate([90, 0, 0])
    linear_extrude(0.1)
    text("8-Port Gigabit Switch", size=4, halign="center", font="Arial:style=Bold");
}

// Power connector
module power_connector() {
    translate([0, 0, -switcher_depth/2 - 0.5])
    difference() {
        color(color_ports)
        cube([10, 8, 1], center=true);

        translate([0, 0, -0.5])
        cylinder(h=2, r=2.5, center=true);
    }
}

// Assemble the complete switcher
body();
front_panel();
top_panel();
side_vents();
bottom();
power_connector();

// 3-Port USB-C Splitter Hub
// Realistic style, medium complexity
// 75 mm x 35 mm x 15 mm overall

// Color definitions
color_body = [0.15,0.15,0.15];        // Dark gray plastic
color_ports = [0.3,0.3,0.3];          // Lighter port plastic
color_led = [0,1,0];                  // Green LED
color_labels = [0.8,0.8,0.8];         // Light gray text

// Main body
module main_body() {
    difference() {
        // Outer shell
        color(color_body)
        minkowski() {
            cube([70,30,11], center=true);
            sphere(1.5);
        }

        // Top recess for logo
        translate([0,0,5.5])
        cube([25,12,2], center=true);

        // Bottom cable relief
        translate([0,0,-5.5])
        cube([60,26,2], center=true);
    }
}

// USB-C port module
module usbc_port(x_pos=0, angle=0) {
    translate([x_pos,0,0])
    rotate([0,angle,0])
    translate([0,-15,0])
    color(color_ports) {
        // Port housing
        difference() {
            cube([8,6,4], center=true);
            translate([0,3.2,0])
            cube([6,0.5,2.5], center=true);
        }

        // Internal contacts
        translate([0,3.5,0])
        color([0.1,0.1,0.1])
        cube([5.5,0.3,1.2], center=true);

        // Port label
        translate([0,3.5,2.5])
        rotate([90,0,0])
        color(color_labels)
        linear_extrude(0.2)
        text("USB-C", size=2, halign="center");
    }
}

// LED indicator
module status_led() {
    translate([25,0,5.5])
    color(color_led)
    cylinder(h=1, d=3, $fn=20);

    // LED lens
    translate([25,0,6.5])
    color([0,1,0,0.5])
    sphere(1.5, $fn=20);
}

// Cable strain relief
module cable_relief() {
    translate([-30,0,-5.5])
    color(color_body)
    difference() {
        cylinder(h=3, d=8, $fn=30);
        translate([0,0,-0.5])
        cylinder(h=4, d=5, $fn=30);
    }
}

// Assembly
main_body();

// Three USB-C ports
usbc_port(-20, 0);
usbc_port(0, 0);
usbc_port(20, 0);

