    difference() {
        cube([6,30,11], center=true);
        translate([0,0,5.5])
        cube([7,32,12], center=true);
    }
}

// Bottom rubber feet
module foot(x,y) {
    translate([x,y,-6.5])
    color([0.05,0.05,0.05])
    cylinder(h=1, d=4, $fn=20);
}

