color_screen = "DarkBlue";
color_buttons = "Gray";
color_text = "White";
color_ports = "DarkGray";

// Main body module
module tuner_body() {
    color(color_body)
    difference() {
        // Main rounded rectangular body
        minkowski() {
            cube([75, 30, 10], center = true);
            sphere(2);
        }

        // Screen cutout
        translate([0, 5, 5.1])
            cube([50, 20, 2], center = true);

        // Button holes
        translate([20, -10, 5.1])
            cylinder(h = 2, r = 3, $fn = 20);
        translate([0, -10, 5.1])
            cylinder(h = 2, r = 3, $fn = 20);
        translate([-20, -10, 5.1])
            cylinder(h = 2, r = 3, $fn = 20);
    }
}

// LCD Screen module
module lcd_screen() {
    color(color_screen)
    translate([0, 5, 5])
    cube([48, 18, 0.5], center = true);

    // Screen details
    color("LightBlue")
    translate([0, 5, 5.3])
    cube([45, 15, 0.2], center = true);

    // Tuning indicator bars
