    translate([-35, 0, 0])
    rotate([0, 90, 0])
    difference() {
        cylinder(h = 8, r1 = 5, r2 = 6, $fn = 30);
        cylinder(h = 8, r1 = 3.5, r2 = 3.5, $fn = 30);
    }

    // Jack hole
    color("Black")
    translate([-39, 0, 0])
    rotate([0, 90, 0])
    cylinder(h = 10, r = 3.5, $fn = 30);
}

// Power LED indicator
