    color(color_body)
    translate([0, 0, -8])
    union() {
        // Clip base
        cube([70, 25, 8], center = true);

        // Clip spring housing
        translate([0, 12, -4])
            cube([60, 6, 4], center = true);

        // Clip teeth
        for(i = [-20:20:40]) {
            translate([i, 12.5, -6])
                cube([15, 1, 2]);
        }
    }
}

// Brand label
module brand_label() {
