// Preamp Electronic Device - Realistic Style with Medium Complexity
// 3D printable preamplifier model with functional features

// Dimensions
body_width = 80;
body_depth = 120;
body_height = 35;
front_panel_thickness = 5;

// Knob dimensions
knob_radius = 8;
knob_height = 10;

// Button dimensions
button_width = 8;
button_height = 4;
button_depth = 3;

// LED dimensions
led_radius = 2;
led_depth = 3;

// RCA jack dimensions
rca_outer_radius = 5;
rca_inner_radius = 2;
rca_depth = 8;

// Main body
module body() {
    color([0.2, 0.2, 0.2])  // Dark gray
    difference() {
        // Main box
        cube([body_width, body_depth, body_height], center=true);

        // Front panel recess
        translate([0, body_depth/2 - front_panel_thickness/2, 0])
            cube([body_width + 1, front_panel_thickness + 1, body_height + 1], center=true);
    }
}

// Front panel
module front_panel() {
    color([0.1, 0.1, 0.1])  // Black
    translate([0, body_depth/2 - front_panel_thickness/2, 0])
        cube([body_width, front_panel_thickness, body_height], center=true);
}

// Volume knob
module volume_knob() {
    color([0.8, 0.8, 0.8])  // Silver
    translate([-body_width/3, body_depth/2 + knob_height/2, 0])
        rotate([90, 0, 0])
            cylinder(h=knob_height, r1=knob_radius, r2=knob_radius*0.9);
}

// Gain knob
module gain_knob() {
    color([0.8, 0.8, 0.8])  // Silver
    translate([body_width/3, body_depth/2 + knob_height/2, 0])
        rotate([90, 0, 0])
            cylinder(h=knob_height, r1=knob_radius, r2=knob_radius*0.9);
}

// Power button
module power_button() {
    color([0.9, 0.1, 0.1])  // Red
    translate([0, body_depth/2 + button_depth/2, body_height/4])
        cube([button_width, button_depth, button_height], center=true);
}

// LED indicators
module leds() {
    for(i = [-1, 1]) {
        color([0, 1, 0])  // Green
        translate([i * body_width/4, body_depth/2 + led_depth/2, -body_height/4])
            sphere(r=led_radius);
    }
}

// RCA input jacks
module rca_jacks() {
    color([0.7, 0.7, 0.7])  // Silver
    for(i = [-1, 1]) {
        translate([i * 15, -body_depth/2 - rca_depth/2, -body_height/4])
            difference() {
                cylinder(h=rca_depth, r=rca_outer_radius);
                translate([0, 0, -1])
                    cylinder(h=rca_depth + 2, r=rca_inner_radius);
            }
    }
}

// RCA output jacks
module rca_outputs() {
    color([0.7, 0.7, 0.7])  // Silver
    for(i = [-1, 1]) {
        translate([i * 15, -body_depth/2 - rca_depth/2, body_height/4])
            difference() {
                cylinder(h=rca_depth, r=rca_outer_radius);
                translate([0, 0, -1])
                    cylinder(h=rca_depth + 2, r=rca_inner_radius);
            }
    }
}

// VU meter
module vu_meter() {
    color([0, 0, 0])  // Black
    translate([0, body_depth/2 + 0.1, 0])
        cube([40, 0.2, 15], center=true);

    color([0.9, 0.9, 0.9])  // White
    translate([0, body_depth/2 + 0.2, 0])
        cube([36, 0.1, 11], center=true);
}

// Ventilation holes on top
module vents() {
    color([0.2, 0.2, 0.2])
    for(i = [-2:2]) {
        translate([i * 15, 0, body_height/2 + 0.1])
            cube([10, 100, 1], center=true);
    }
}

// Rubber feet
module feet() {
    color([0.1, 0.1, 0.1])  // Black
    for(x = [-1, 1]) {
        for(y = [-1, 1]) {
            translate([x * body_width/2.5, y * body_depth/2.5, -body_height/2 - 2])
                cylinder(h=4, r=5);
        }
    }
}

// Logo placeholder
module logo() {
    color([0.8, 0.8, 0.8])
    translate([body_width/2 - 15, body_depth/2 - 15, body_height/2 + 0.1])
        cube([20, 10, 0.5]);
}

// Assemble the preamp
union() {
    body();
    front_panel();
    volume_knob();
    gain_knob();
    power_button();
    leds();
    rca_jacks();
    rca_outputs();
    vu_meter();
    vents();
    feet();
    logo();
}

// Deck electronic device - Realistic portable audio recorder
// Main body dimensions: 70mm x 40mm x 15mm

// Colors
recorder_body = [0.2, 0.2, 0.2];      // Dark gray body
screen_color = [0.1, 0.1, 0.1];       // Black screen
button_color = [0.3, 0.3, 0.3];       // Medium gray buttons
accent_color = [0.8, 0.1, 0.1];       // Red accent
metal_color = [0.6, 0.6, 0.6];        // Silver metal

// Main body
module recorder_body() {
    difference() {
        // Main shell
        color(recorder_body)
        rounded_cube([70, 40, 15], 2);

        // Top display recess
        translate([5, 5, 12])
            cube([60, 15, 3]);

        // Button recesses
        for (x = [0:1:6]) {
            translate([8 + x*8, 25, 12])
                cube([6, 10, 3]);
        }
    }
}

// Create rounded cube
module rounded_cube(size, radius) {
    hull() {
        for (x = [0, 1])
            for (y = [0, 1])
                for (z = [0, 1])
                    translate([x*(size[0]-2*radius), y*(size[1]-2*radius), z*(size[2]-2*radius)])
                        sphere(r=radius);
    }
}

// Display screen
module display() {
    color(screen_color)
    translate([7, 6, 12.5])
        cube([58, 13, 0.5]);

    // Screen bezel
    color(metal_color)
    translate([6.5, 5.5, 12.2])
        cube([59, 14, 0.3]);
}

// Control buttons
module buttons() {
    // Record button (red)
    color(accent_color)
    translate([10, 30, 12.5])
        cylinder(h=1.5, r=3);

    // Play/pause buttons
    for (i = [0:1:3]) {
        color(button_color)
        translate([20 + i*12, 30, 12.5])
            cube([6, 6, 1.5]);
    }

    // Volume wheel
