// Gimbal Electronic Device - Realistic Style, Medium Complexity
// Size: ~70mm diameter, designed for 3D printing

// Main parameters
$fn = 32;
wall_thickness = 2;
body_radius = 35;
body_height = 15;
motor_diameter = 12;
motor_height = 8;
arm_length = 25;
arm_thickness = 4;
screen_width = 20;
screen_height = 12;

// Color definitions
color_body = [0.2, 0.2, 0.2];
color_motor = [0.8, 0.8, 0.8];
color_arm = [0.4, 0.4, 0.4];
color_screen = [0.1, 0.1, 0.1];
color_button = [0.9, 0.9, 0.9];

// Main gimbal assembly
module gimbal_device() {
    // Central body with ports and indicators
    color(color_body) {
        difference() {
            cylinder(h = body_height, r = body_radius);

            // Micro USB port
            translate([body_radius - 3, 0, body_height/2])
                rotate([0, 90, 0])
                    cube([6, 8, 4], center = true);

            // LED indicator hole
            translate([body_radius - 2, -8, body_height - 3])
                cylinder(h = 3, r = 2);

            // Power button hole
            translate([-body_radius + 5, 0, body_height/2])
                cube([4, 6, 4], center = true);
        }

        // Top grip surface
        translate([0, 0, body_height])
            cylinder(h = 2, r = body_radius - 2);
    }

    // First motor (roll axis)
    translate([0, 0, body_height + motor_height/2])
        motor_assembly();

    // First arm
    color(color_arm) {
        translate([0, 0, body_height + motor_height])
            rotate([90, 0, 0])
                cube([arm_thickness, arm_length, arm_thickness], center = true);
    }

    // Second motor (pitch axis)
    translate([0, arm_length/2, body_height + motor_height])
        rotate([90, 0, 0])
            motor_assembly();

    // Second arm
    color(color_arm) {
        translate([0, arm_length, body_height + motor_height])
            cube([arm_length, arm_thickness, arm_thickness], center = true);
    }

    // Third motor (yaw axis)
    translate([arm_length/2, arm_length, body_height + motor_height])
        rotate([0, 90, 0])
            motor_assembly();

    // Camera mount platform
    color(color_body) {
        translate([arm_length/2, arm_length + arm_thickness, body_height + motor_height])
            cube([30, 20, 3], center = true);
    }

    // OLED screen
    color(color_screen) {
        translate([0, 0, body_height - 1])
            cube([screen_width, screen_height, 0.5], center = true);
    }

    // Control buttons
    for(i = [0:2]) {
        color(color_button) {
            translate([-body_radius/2 + 5 + i*6, body_radius - 3, body_height/2])
                cylinder(h = 2, r = 2);
        }
    }

    // Battery compartment cover
    translate([0, -body_radius + 5, 1])
        cube([25, 8, 0.5], center = true);
}

// Motor assembly module
module motor_assembly() {
    color(color_motor) {
        difference() {
            cylinder(h = motor_height, r = motor_diameter);

            // Cooling vents
            for(i = [0:3]) {
                rotate([0, 0, i*90])
                    translate([motor_diameter - 1, 0, motor_height/2])
                        cube([2, 4, motor_height], center = true);
            }
        }

        // Motor shaft
        cylinder(h = 3, r = 2);
    }
}

// Render the gimbal
gimbal_device();

// Tripod Electronic Device - Medium Realistic Style
// A compact camera tripod with electronic features

// Global parameters
tripod_height = 80;
