// Module for tripod leg
module tripod_leg(length, width, angle) {
    translate([0, 0, 0])
    rotate([angle, 0, 0])
    color([0.2, 0.2, 0.2])  // Dark gray aluminum
    difference() {
        // Main leg tube
        cylinder(h=length, d1=width, d2=width*0.8, $fn=16);

        // Telescoping section
        translate([0, 0, length-15])
        cylinder(h=15, d1=width*0.7, d2=width*0.7, $fn=16);
    }

    // Leg foot with rubber grip
    translate([0, 0, length])
    color([0.1, 0.1, 0.1])  // Black rubber
    sphere(d=width*1.5, $fn=16);
}

// Module for camera mount plate
module camera_mount() {
    color([0.3, 0.3, 0.3])  // Aluminum color
    difference() {
        // Main mounting plate
        cylinder(h=5, d=mount_diameter, $fn=32);

        // 1/4-20 threaded insert hole
        translate([0, 0, -1])
        cylinder(h=8, d=4, $fn=16);

        // Anti-slip grooves
        for(i=[-2:1:2]) {
            translate([i*5, 0, 2.5])
            cube([1, 20, 1], center=true);
        }
    }

    // Electronic bubble level
    translate([0, 8, 3])
    color([0.1, 0.1, 0.1])  // Black plastic housing
    cube([15, 6, 3], center=true);

    // Bubble level window
    translate([0, 8, 4.6])
    color([0.3, 0.6, 0.9, 0.8])  // Transparent blue
    cube([13, 4, 0.5], center=true);
}

// Module for electronic control unit
module control_unit() {
    // Main control body
    color([0.15, 0.15, 0.15])  // Dark plastic
    difference() {
        cube([35, 20, 12], center=true);

        // LCD screen recess
        translate([0, 5, 6.1])
        cube([25, 10, 1], center=true);
    }

    // LCD screen
    translate([0, 5, 7])
    color([0.2, 0.2, 0.2])  // Dark screen
    cube([23, 8, 0.5], center=true);

    // Navigation buttons
    for(i=[-1:1:1]) {
        translate([10*i, -7, 7])
        color([0.4, 0.4, 0.4])  // Gray buttons
        cylinder(h=1.5, d=6, $fn=16);
    }

    // USB-C port
    translate([18, 0, 0])
    color([0.1, 0.1, 0.1])
    cube([8, 2, 4], center=true);
}

// Module for central hub
module central_hub() {
    color([0.25, 0.25, 0.25])  // Aluminum
    difference() {
        // Main hub body
        cylinder(h=base_height, d=30, $fn=32);

        // Cable management hole
        translate([0, -12, 7])
        rotate([90, 0, 0])
        cylinder(h=25, d=6, $fn=16);
    }

    // LED status ring
    translate([0, 0, base_height-2])
    color([0.1, 0.3, 0.8, 0.7])  // Blue LED glow
    cylinder(h=1, d=28, $fn=32);
}

// Main assembly
union() {
    // Central hub
    central_hub();

    // Control unit mounted on hub
    translate([0, 20, base_height-6])
    rotate([0, 0, 0])
    control_unit();

    // Camera mount on top
    translate([0, 0, base_height])
    camera_mount();

    // Three legs
    for(i=[0:120:360]) {
        rotate([0, 0, i])
        translate([10, 0, 0])
        tripod_leg(leg_length, leg_width, -30);
    }

    // Battery compartment access panel
    translate([0, -15, base_height-6])
    color([0.3, 0.3, 0.3])
    cube([12, 8, 2], center=true);
}

// JIB Electronic Device (portable sound recorder / field recorder)
// 60 mm x 32 mm x 15 mm â€“ fits on most FDM beds
module jib_recorder() {
    /* ---------- Main Body ---------- */
    // Main shell â€“ printed on its back so no overhangs
    difference() {
        // Outer shell (rounded block)
        minkowski() {
            cube([60, 32, 10], center=true);
            sphere(2);        // 2 mm fillets all around
        }
        // Hollow out for electronics
        translate([0,0,-1])
            cube([56, 28, 8], center=true);
    }

    /* ---------- Top Face ---------- */
    translate([0,0,5]) {
        color("silver") {
            // 1 mm aluminium top plate
            cube([60, 32, 1], center=true);
        }

        // Screen
        translate([-20, 8, 0.6])
            color("black")
                cube([22, 12, 0.4]);

        // 5 push-buttons (array)
        for (x=[-30:15:-15])
            translate([x, -8, 0.6])
                button();

        // Record button (large red)
        translate([20, -8, 0.6])
            color("red")
                cylinder(h=1.2, d=6, $fn=20);
    }

    /* ---------- Front Face (when lying on back) ---------- */
    translate([0, -16, 0])
        rotate([90,0,0]) {
            color("dimgray") {
                // Protective grille for microphones
                difference() {
                    cube([50, 10, 2], center=true);
                    for (x=[-20:4:20])
                        translate([x, 0, -1])
                            cube([1.5, 12, 4]);
                }
            }

            // 3.5 mm jack port
            translate([-24, 0, 0])
                cylinder(h=8, d=6, $fn=20);

            // USB-C port
            translate([24, 0, 0])
                cube([8, 2.6, 3.2], center=true);
        }

    /* ---------- Battery Cover ---------- */
    translate([0, 0, -6.5])
        color("darkgray")
            cube([30, 20, 1], center=true);

    /* ---------- Logo ---------- */
    translate([0, 12, 5.6])
        rotate([0,0,180])
            linear_extrude(height=0.2)
                text("JIB", size=4, halign="center");
}

/* ---------- Re-usable button ---------- */
module button() {
    color("darkslategray")
        cylinder(h=1, d=4, $fn=20);
    color("lightblue")
        translate([0,0,1])
            cylinder(h=0.2, d=3.5, $fn=20);
}

