            cube([body_w, body_h, body_d], center=true);
            sphere(2);
        }

        // Hollow out for electronics
        color("silver")
        translate([0, 0, -1])
        minkowski() {
            cube([body_w-4, body_h-4, body_d-2], center=true);
            sphere(1.5);
        }

        // Screen cutout
        color("silver")
        translate([0, 0, body_d/2 - screen_d/2])
