    cylinder(h=button_d, r=button_w/2, $fn=20);

    // Channel up/down buttons
    for(i=[0:1]) {
        translate([body_w/2 - 15, -body_h/2 + 10 + i*8, body_d/2 - button_d/2])
        color("darkgray")
        cube([button_w, button_h, button_d], center=true);
    }

    // Menu buttons
    for(i=[0:2]) {
        translate([0, -body_h/2 + 8 + i*10, body_d/2 - button_d/2])
        color("lightgray")
        cube([button_w-2, button_h-2, button_d], center=true);
    }
}

// Connection ports
module ports() {
    // HDMI ports
    for(i=[0:1]) {
        translate([body_w/2 - port_w/2 - i*(port_w+5), 0, -body_d/2 + port_d/2])
        color("black")
        cube([port_w, port_h, port_d], center=true);
