// Realistic Matrix Electronic Device - LED Matrix Display Module
// Medium complexity with proper 3D printing considerations

// Overall dimensions
module_matrix_width = 80;
module_matrix_height = 80;
module_thickness = 12;

// Wall thickness for 3D printing
wall_thickness = 1.2;

// LED matrix parameters
led_rows = 8;
led_cols = 8;
led_diameter = 6;
led_depth = 2;
led_spacing = module_matrix_width / (led_cols + 1);

// Mounting holes
mount_hole_diameter = 3.2;
mount_hole_offset = 5;

// PCB color
pcb_color = [0.1, 0.6, 0.1];

// Main module body
module body() {
    difference() {
        // Main PCB board
        color(pcb_color)
        cube([module_matrix_width, module_matrix_height, module_thickness], center=true);

        // LED matrix holes
        for (i = [1:led_cols]) {
            for (j = [1:led_rows]) {
                translate([
                    -module_matrix_width/2 + i * led_spacing,
                    -module_matrix_height/2 + j * led_spacing,
                    0
                ])
                cylinder(h=module_thickness + 2, d=led_diameter, center=true);
            }
        }

        // Mounting holes - corners
        for (x = [-1, 1]) {
            for (y = [-1, 1]) {
                translate([
                    x * (module_matrix_width/2 - mount_hole_offset),
                    y * (module_matrix_height/2 - mount_hole_offset),
                    0
                ])
                cylinder(h=module_thickness + 2, d=mount_hole_diameter, center=true);
            }
        }
    }
}

// LED pixels
module led_pixels() {
    for (i = [1:led_cols]) {
        for (j = [1:led_rows]) {
            translate([
                -module_matrix_width/2 + i * led_spacing,
                -module_matrix_height/2 + j * led_spacing,
                module_thickness/2 - led_depth/2
            ])
            color([0.9, 0.1, 0.1])
            cylinder(h=led_depth, d=led_diameter * 0.8);

            // LED lens dome
            translate([
                -module_matrix_width/2 + i * led_spacing,
                -module_matrix_height/2 + j * led_spacing,
                module_thickness/2 + led_depth/2
            ])
            color([0.7, 0.7, 1])
            sphere(d=led_diameter * 0.6);
        }
    }
}

// Control chip
module control_chip() {
    translate([0, -module_matrix_height/2 + 15, module_thickness/2 - 1])
    color([0.2, 0.2, 0.2])
    cube([20, 8, 2], center=true);

    // Chip markings
    translate([0, -module_matrix_height/2 + 15, module_thickness/2 + 0.2])
    color([0.1, 0.1, 0.1])
    cube([18, 6, 0.1], center=true);
}

// Power connector
module power_connector() {
    translate([-module_matrix_width/2 + 10, 0, module_thickness/2 - 1])
    color([0.3, 0.3, 0.3])
    cube([8, 12, 4], center=true);

    // Connector pins
    for (i = [-1, 1]) {
        translate([-module_matrix_width/2 + 10, i * 3, module_thickness/2 + 1])
        color([0.8, 0.8, 0.8])
        cylinder(h=3, d=1.2);
    }
}

// Data connector
module data_connector() {
    translate([module_matrix_width/2 - 10, 0, module_thickness/2 - 1])
    color([0.3, 0.3, 0.3])
    cube([12, 8, 4], center=true);

    // Connector holes
    for (i = [-1.5, 0, 1.5]) {
        translate([module_matrix_width/2 - 10, i * 2, module_thickness/2 + 1])
        color([0.1, 0.1, 0.1])
        cylinder(h=3, d=1.5);
    }
}

// Silkscreen labels
module silkscreen_labels() {
    // VCC label
    translate([-module_matrix_width/2 + 10, 8, module_thickness/2 + 0.1])
    color([0.9, 0.9, 0.9])
    cube([6, 2, 0.1], center=true);

    // GND label
    translate([-module_matrix_width/2 + 10, -8, module_thickness/2 + 0.1])
    color([0.9, 0.9, 0.9])
    cube([6, 2, 0.1], center=true);
}

// Assembly
union() {
    body();
    led_pixels();
    control_chip();
    power_connector();
    data_connector();
    silkscreen_labels();
}

