// Rounded box helper
module rounded_box(w, h, d, r) {
    hull() {
        translate([r, r, 0]) cylinder(h=d, r=r);
        translate([w-r, r, 0]) cylinder(h=d, r=r);
        translate([r, h-r, 0]) cylinder(h=d, r=r);
        translate([w-r, h-r, 0]) cylinder(h=d, r=r);
