// Isolator Electronic Device - 3D Printable Realistic Model
// Medium complexity OpenSCAD design for EMI isolator

$fn = 30;

// Colors
color_metallic = [0.7, 0.7, 0.75];
color_black = [0.1, 0.1, 0.1];
color_red = [0.8, 0.1, 0.1];
color_yellow = [0.8, 0.8, 0.1];
color_green = [0.1, 0.7, 0.1];

// Main dimensions
base_length = 80;
base_width = 45;
base_height = 8;
case_height = 25;
total_height = base_height + case_height;

// PCB standoffs
standoff_height = 3;
standoff_diameter = 5;

// Connector dimensions
bnc_diameter = 12;
bnc_length = 15;
led_diameter = 3;
led_depth = 2;

// Create isolator base
module isolator_base() {
    color(color_metallic)
    difference() {
        // Main base block
        cube([base_length, base_width, base_height], center=true);

        // Mounting holes
        for(x = [base_length/2 - 10, -base_length/2 + 10])
            for(y = [base_width/2 - 10, -base_width/2 + 10])
                translate([x, y, -base_height/2])
                    cylinder(h=base_height+1, d=3.5, center=true);
    }
}

// Main case body
module case_body() {
    color(color_black)
    translate([0, 0, base_height/2 + case_height/2])
        cube([base_length-4, base_width-4, case_height], center=true);
}

// PCB standoffs inside
module pcb_standoffs() {
    color(color_black)
    for(x = [base_length/2 - 15, -base_length/2 + 15])
        for(y = [base_width/2 - 15, -base_width/2 + 15])
            translate([x, y, base_height - standoff_height/2])
                cylinder(h=standoff_height, d=standoff_diameter, center=true);
}

// BNC connectors
module bnc_connector(x_pos, y_pos, z_pos, rotation=0) {
    color(color_metallic)
    rotate([0, rotation, 0])
        translate([x_pos, y_pos, z_pos])
        union() {
            // Main barrel
            cylinder(h=bnc_length, d=bnc_diameter, center=true);

            // Insulator
            color(color_red)
            translate([0, 0, -bnc_length/2 + 3])
                cylinder(h=2, d=8, center=true);

            // Center pin
            color(color_metallic)
            translate([0, 0, -bnc_length/2 + 1])
                cylinder(h=4, d=2, center=true);

            // Hex nut
            rotate([0, 0, 0])
                translate([0, 0, bnc_length/2 - 3])
                    cylinder(h=3, d=14, $fn=6, center=true);
        }
}

// LED indicators
module led_indicator(x_pos, y_pos, z_pos, led_color) {
    color(led_color)
    translate([x_pos, y_pos, z_pos])
        cylinder(h=led_depth, d=led_diameter, center=true);
}

// Ventilation slots
module ventilation_slots() {
    color(color_black)
    difference() {
        // Main case
        translate([0, 0, base_height/2 + case_height/2])
            cube([base_length-4, base_width-4, case_height], center=true);

        // Ventilation slots
        for(i = [-3:3]) {
            translate([i*10, 0, base_height + case_height/2])
                cube([2, base_width, case_height-2], center=true);
        }
    }
}

// Label area
module label_area() {
    color([0.9, 0.9, 0.9])
    translate([0, base_width/2 - 8, base_height + 2])
        cube([base_length - 10, 6, 0.5], center=false);
}

// Assembly
union() {
    // Base components
    isolator_base();
    case_body();
    ventilation_slots();
