module rounded_cube(size, radius, center = false) {
    x = size.x; y = size.y; z = size.z;
    translate(center ? [-x/2, -y/2, -z/2] : [0, 0, 0]) {
        hull() {
            translate([radius, radius, 0]) cylinder(h=z, r=radius);
            translate([x-radius, radius, 0]) cylinder(h=z, r=radius);
            translate([radius, y-radius, 0]) cylinder(h=z, r=radius);
            translate([x-radius, y-radius, 0]) cylinder(h=z, r=radius);
        }
        hull() {
            translate([0, radius, radius]) rotate([0, 90, 0]) cylinder(h=x, r=radius);
            translate([0, y-radius, radius]) rotate([0, 90, 0]) cylinder(h=x, r=radius);
            translate([0, radius, z-radius]) rotate([0, 90, 0]) cylinder(h=x, r=radius);
            translate([0, y-radius, z-radius]) rotate([0, 90, 0]) cylinder(h=x, r=radius);
        }
        hull() {
            translate([radius, 0, radius]) rotate([90, 0, 0]) cylinder(h=y, r=radius);
            translate([x-radius, 0, radius]) rotate([90, 0, 0]) cylinder(h=y, r=radius);
            translate([radius, 0, z-radius]) rotate([90, 0, 0]) cylinder(h=y, r=radius);
            translate([x-radius, 0, z-radius]) rotate([90, 0, 0]) cylinder(h=y, r=radius);
        }
    }
}

