// Realistic 3D printable diode model, medium complexity (~85 mm long)
// Main body: glass tube with axial leads and realistic band marking

// Color constants -----------------------------------------------------------
color_lead = "Silver";
color_glass = "LightSkyBlue";
color_band = "White";

// Dimensions (in mm) ---------------------------------------------------------
D_lead = 1.0;           // wire diameter
L_lead = 20;            // exposed lead length
D_body = 5.6;           // standard DO-41 glass body
L_body = 9.0;           // glass length
band_w = 1.2;           // cathode band width
band_t = 0.15;          // band thickness (paint layer)

// Build the complete diode ----------------------------------------------------
module diode() {
    // Left lead (cathode)
    color(color_lead) translate([-L_lead, 0, 0]) cylinder(h = L_lead, d = D_lead, $fn = 16);

    // Right lead (anode)
    color(color_lead) translate([L_body, 0, 0]) cylinder(h = L_lead, d = D_lead, $fn = 16);

    // Glass body -----------------------------------------------------------
    color(color_glass) difference() {
        // Main glass cylinder
        cylinder(h = L_body, d = D_body, center = false, $fn = 60);

        // Internal hollow space for realism (optional, saves material)
        translate([0, 0, -0.1])
            cylinder(h = L_body + 0.2, d = D_body - 1.2, center = false, $fn = 60);
    }

    // Cathode identification band ------------------------------------------
    color(color_band)
        translate([L_body - 2.5 - band_w/2, 0, D_body/2 + band_t/2])
        cube([band_w, D_body + 0.1, band_t], center = true);

    // Realistic rounded ends on glass (domes) -------------------------------
    color(color_glass) {
        translate([0, 0, 0])
            sphere(d = D_body, $fn = 60);
        translate([L_body, 0, 0])
            sphere(d = D_body, $fn = 60);
    }
}

// Optional stand for better print orientation -------------------------------
module stand() {
    color("Gray")
        difference() {
            translate([-5, -3, -5]) cube([L_body + 10, 6, 5]);  // base
            translate([-4, -2.5, -4.9]) cube([L_body + 8, 5, 5]);  // hollow
        }
}

