// Realistic 3D printable inductor model
// Through-hole axial inductor with 10mm diameter, 20mm length
// Includes wire leads, ferrite core, and copper windings

// Color definitions
color_core = [0.4, 0.4, 0.4];      // Dark gray ferrite
color_wire = [0.8, 0.5, 0.2];      // Copper wire
color_coating = [0.1, 0.1, 0.1];   // Black epoxy coating
color_leads = [0.7, 0.7, 0.7];     // Silver leads

// Main parameters
inductor_diameter = 12;      // Overall diameter with coating
core_diameter = 8;           // Ferrite core diameter
inductor_length = 20;        // Body length
wire_diameter = 0.5;         // Copper wire thickness
lead_diameter = 1.0;         // Lead wire thickness
lead_length = 15;            // Exposed lead length
windings = 20;               // Number of wire loops
coating_thickness = 1;       // Epoxy coating thickness

module ferrite_core() {
    color(color_core)
    cylinder(h=inductor_length, d=core_diameter, $fn=32);
}

module copper_windings() {
    color(color_wire) {
        for (i = [0:windings-1]) {
            rotate([0, 90, i * (360/windings)])
            translate([0, core_diameter/2 + wire_diameter/2, 0])
            rotate([90, 0, 0])
            torus(wire_diameter/2, inductor_length/2 - 2);
        }
    }
}

module torus(r, R) {
    rotate_extrude($fn=16)
    translate([R, 0, 0])
    circle(r=r, $fn=16);
}

module epoxy_coating() {
    difference() {
        color(color_coating)
        cylinder(h=inductor_length + 2, d=inductor_diameter, $fn=32);

        // Hollow out for core and windings
        translate([0, 0, -1])
        cylinder(h=inductor_length + 4, d=core_diameter + wire_diameter*2 + 0.5, $fn=32);
    }
}

module lead_wire(side) {
    color(color_leads) {
        translate([0, 0, side * (inductor_length/2 + lead_length/2)])
        cylinder(h=lead_length, d=lead_diameter, $fn=16);

        // Lead bend
        translate([0, side * lead_length/2, side * (inductor_length/2 + lead_length)])
        rotate([90, 0, 0])
        cylinder(h=lead_length/2, d=lead_diameter, $fn=16);
    }
}

module marking_band() {
    color([1, 1, 1])
    translate([0, inductor_diameter/2, inductor_length/2 - 2])
    cube([0.5, 0.1, 4], center=true);
}

// Assembly
union() {
    // Central core
    ferrite_core();

    // Copper windings around core
    copper_windings();

    // Protective epoxy coating
    epoxy_coating();

    // Lead wires
    lead_wire(-1);  // Bottom lead
    lead_wire(1);   // Top lead

    // Identification band
    marking_band();
}

// Realistic 3D printable resistor with color bands and leads
// Overall length ~80 mm, diameter ~12 mm

// Basic parameters
$fn = 30;
body_length = 60;
body_diameter = 12;
lead_diameter = 1.2;
lead_length = 15;
band_width = 2.2;
band_gap = 1.0;
wall = 1.5;

// Resistor color code (4-band 1 kÎ©: brown-black-red-gold)
colors = [
    [0.6, 0.3, 0.0],  // brown (1)
    [0.2, 0.2, 0.2],  // black (0)
    [1.0, 0.0, 0.0],  // red (Ã—100)
    [0.9, 0.7, 0.2]   // gold (Â±5 %)
];

// Main body
module resistor_body() {
    difference() {
        // Solid cylinder
        color([0.7, 0.45, 0.25])  // beige body
        cylinder(h = body_length, d = body_diameter, center = true);

        // Hollow center for printability
        color([0.7, 0.45, 0.25])
        cylinder(h = body_length + 1, d = body_diameter - 2 * wall, center = true);
    }
}

// Color band
module color_band(pos, col) {
    translate([pos, 0, 0])
    rotate([0, 90, 0])
    color(col)
    cylinder(h = band_width, d = body_diameter + 0.1, center = true);
}

// All color bands
module bands() {
    band_start = -(body_length / 2) + 5;
    for (i = [0 : len(colors) - 1]) {
        color_band(band_start + i * (band_width + band_gap), colors[i]);
    }
}

// Lead wire
module lead(end) {
    translate([end, 0, 0])
    color([0.7, 0.7, 0.7])
    cylinder(h = lead_length, d = lead_diameter);
}

// Lead termination cup
module cup(end) {
    translate([end, 0, 0])
    difference() {
        color([0.5, 0.5, 0.5])
        cylinder(h = 2, d = body_diameter * 0.8);

        color([0.5, 0.5, 0.5])
        cylinder(h = 3, d = body_diameter * 0.5);
    }
}

// Assembly
union() {
    resistor_body();
    bands();
    cup(-body_length / 2 - 1);
    cup(body_length / 2 + 1);
    lead(-body_length / 2 - lead_length);
    lead(body_length / 2 + lead_length);
}

