    color(pcb_color)
    difference() {
        // Main board
        translate([0, 0, body_h/2])
            cube([body_w, body_d, body_h], center=true);

        // Mounting holes
        for (x = [-1, 1])
            for (y = [-1, 1])
                translate([x*(body_w/2 - screw_offset - screw_r), 
                          y*(body_d/2 - screw_offset - screw_r), 
