// ADC chip (central IC)
module adc_chip() {
    color("#333333")
    translate([0, 0, body_h])
        cube([12, 12, 2], center=true);
}

// Pin headers
module pin_header(x_pos, y_pos, pins) {
    color("#C0C0C0")
    translate([x_pos, y_pos, body_h + 1.5])
        cube([pins * 2.54 + 1.5, 2.5, 5.5], center=true);

    // Individual pins
    for (i = [0:pins-1]) {
        translate([x_pos + (i - (pins-1)/2) * 2.54, y_pos, body_h + 4.25])
            cylinder(h=3, r=0.3, center=true);
    }
}

// Voltage regulator
module regulator() {
    color("#666666")
    translate([-15, 10, body_h + 0.5])
        cube([8, 6, 3], center=true);
}

// Decoupling capacitors
module capacitor(x, y) {
    color("#C0C0C0")
    translate([x, y, body_h + 1])
        cylinder(h=5, r=1.5, center=true);

    color("#CD853F")
    translate([x, y, body_h + 3.5])
        cylinder(h=2, r=2, center=true);
