// Main body shell
module flashBody() {
    color(c_body) union() {
        // main shell
        translate([0, 0, body_h/2])
            cube([body_l, body_w, body_h], center=true);

        // rounded edges
        hull() {
            for(x=[-body_l/2+2, body_l/2-2])
                translate([x, 0, body_h/2])
                    cylinder(h=body_h, r=body_w/2, center=true);
        }

        // activity LED window
        translate([body_l/2-5, body_w/2-1.5, body_h/2+0.1])
            cube([2, 3, 0.2]);

        // key-ring loop
        translate([-body_l/2-0.5, 0, body_h/2+ring_h/2])
            cylinder(h=ring_h, d=ring_d, center=true);
    }
}

// USB Type-A plug
module usbPlug() {
    color(c_metal)
    difference() {
        // metal shell
        translate([body_l/2+usb_l/2, 0, body_h/2])
            cube([usb_l, usb_w, usb_h], center=true);

        // hollow cavity
        translate([body_l/2+usb_l/2, 0, body_h/2])
            cube([usb_l+0.2, usb_w-2*usb_shell_t, usb_h-2*usb_shell_t], center=true);

        // plastic tongue
        color(c_body)
            translate([body_l/2+usb_l/2+1, 0, body_h/2])
                cube([usb_l-2, usb_w-4, usb_h/2-0.5], center=true);
    }
}

// Detachable cap
module cap() {
    color(c_body) difference() {
        // outer shell
        translate([body_l+cap_l/2, 0, body_h/2])
            cube([cap_l, usb_w+2*cap_wall, usb_h+2*cap_wall], center=true);

        // inner cavity
        translate([body_l+cap_l/2, 0, body_h/2])
            cube([cap_l+0.5, usb_w+0.4, usb_h+0.4], center=true);

        // grip ridges
        for(i=[-1:1])
            translate([body_l+cap_l/2-2, i*4, body_h/2+cap_wall+usb_h/2+0.5])
                cube([cap_l-4, 0.8, 0.8], center=true);
    }
}

