dial_pos_y = body_h/2 - 30;

// Probe socket parameters
socket_r = 5;
socket_h = 8;
socket_spacing = 15;

// Color definitions
color_body = [0.2, 0.2, 0.2];
color_screen = [0.1, 0.1, 0.1];
color_display = [0.1, 0.3, 0.1];
color_buttons = [0.8, 0.8, 0.8];
color_dial = [0.5, 0.5, 0.5];
color_ports = [0.3, 0.3, 0.3];

// Main body with rounded edges
module main_body() {
    color(color_body)
    union() {
        // Main box
        difference() {
            minkowski() {
                cube([body_w, body_h, body_d], center=true);
                sphere(r=3);
            }
            // Hollow interior for electronics
            translate([0, 0, 0])
            cube([body_w-4, body_h-4, body_d-4], center=true);
        }

        // Back cover with vents
        translate([0, 0, -body_d/2-1])
        cube([body_w-2, body_h-2, 2], center=true);

        // Vent slots
        for(i = [-30:10:30]) {
            translate([0, i, -body_d/2])
            cube([body_w-10, 2, 1], center=true);
        }
    }
}

// LCD Screen
module lcd_screen() {
    translate([0, body_h/4, body_d/2-1])
    union() {
        // Screen bezel
        color(color_screen)
        difference() {
            cube([screen_w+6, screen_h+6, screen_d+2], center=true);
            cube([screen_w+2, screen_h+2, screen_d+3], center=true);
        }

        // Actual LCD
        color(color_display)
        translate([0, 0, 1])
        cube([screen_w, screen_h, screen_d], center=true);

        // Screen segments for 7-segment display
        color([0, 0.8, 0])
        for(seg_y = [-5, 0, 5]) {
            translate([-20, seg_y, screen_d/2+1])
            cube([30, 2, 0.5]);
        }
    }
}

// Function buttons
module function_buttons() {
    translate([0, -body_h/4, body_d/2])
    for(i = [-1:1]) {
        translate([i * button_spacing, 0, 0])
        color(color_buttons)
        cylinder(r=button_r, h=button_h+1);
    }

    // Additional buttons
    translate([-body_w/4, -body_h/3, body_d/2])
    for(j = [0:2]) {
        translate([j * button_spacing, 0, 0])
        color(color_buttons)
        cylinder(r=button_r*0.8, h=button_h);
    }
}

// Mode selection dial
module selection_dial() {
    translate([dial_pos_x, dial_pos_y, body_d/2])
    rotate([90, 0, 0])
    union() {
        // Dial base
        color(color_dial)
        cylinder(r=dial_r, h=dial_h);

        // Dial grip
        for(i = [0:30:359]) {
            rotate([0, 0, i])
            translate([dial_r-2, 0, dial_h/2])
            cube([4, 2, dial_h], center=true);
        }

        // Dial pointer
        color([1, 0, 0])
        translate([dial_r-1, 0, dial_h+1])
        rotate([0, 90, 0])
        cylinder(r=1, h=5);
    }

    // Dial markings
    for(mark = [-90:30:90]) {
        rotate([0, 0, mark])
        translate([dial_pos_x, dial_pos_y+dial_r+5, body_d/2])
        color([1, 1, 1])
        cube([1, 5, 0.5]);
    }
}

// Input jacks for probes
module probe_jacks() {
    translate([body_w/2-15, -body_h/2+20, 0])
    rotate([90, 0, 0])
    for(pos = [0:1]) {
        translate([0, pos * socket_spacing, 0])
        union() {
            // Jack housing
            color(color_ports)
            cylinder(r=socket_r+2, h=socket_h);

            // Jack hole
            translate([0, 0, -1])
            cylinder(r=socket_r, h=socket_h+2);

            // Color coding
            translate([0, 0, socket_h-1])
            color(pos == 0 ? [1, 0, 0] : [0, 0, 1])
            cylinder(r=socket_r+1, h=1);
        }
    }

    // Labels
    translate([body_w/2-12, -body_h/2+15, 2])
    rotate([0, 0, 90])
    color([1, 1, 1])
    union() {
        translate([0, 0, 0]) cube([2, 3, 0.5]);
        translate([0, 15, 0]) cube([2, 3, 0.5]);
    }
}

// Battery compartment
module battery_compartment() {
    translate([0, body_h/2-20, -body_d/2+5])
    difference() {
        // Cover
        color(color_body)
        cube([30, 15, 2], center=true);

        // Screws
        for(screw_x = [-10, 10]) {
            translate([screw_x, 0, -1])
            cylinder(r=1, h=4);
        }
    }
}

// Main assembly
union() {
    main_body();
    lcd_screen();
    function_buttons();
    selection_dial();
    probe_jacks();
    battery_compartment();
}

