// Module for camera body
module camera_body() {
    color(color_camera)
    difference() {
        // Main body
        translate([-camera_w/2, -camera_h/2, 0])
            cube([camera_w, camera_h, camera_d]);

        // Lens hole
        translate([0, 0, camera_d])
            cylinder(h=lens_h+1, d=lens_d+2);

        // Mounting holes (4 corners)
        for(x = [-9, 9])
            for(y = [-8, 8])
                translate([x, y, -0.5])
                    cylinder(h=camera_d+1, d=2);
    }
}

// Module for lens assembly
module lens_assembly() {
    // Main lens barrel
    color(color_lens)
    translate([0, 0, camera_d])
        cylinder(h=lens_h, d=lens_d);

    // Lens glass
    color([0.1, 0.1, 0.3, 0.7])
    translate([0, 0, camera_d+lens_h-0.5])
        cylinder(h=0.5, d=lens_d-2);

    // Focus ring
    color(color_camera)
    translate([0, 0, camera_d+1])
        cylinder(h=2, d=lens_d+2);
}

// Module for status LED
module status_led() {
    color(color_led)
    translate([8, -camera_h/2-1, camera_d-1])
        sphere(d=1.5);
}

// Module for mount base
module mount_base() {
    color(color_mount)
    difference() {
        // Main base
        translate([-base_w/2, -base_h/2, 0])
            cube([base_w, base_h, base_d]);

        // Cable channel
        translate([-camera_w/2-5, -camera_h/2-5, 0])
            cube([camera_w+10, 5, base_d]);

        // Mounting holes for tripod
        for(x = [-20, 20])
            for(y = [-15, 15])
                translate([x, y, -0.5])
                    cylinder(h=base_d+1, d=3);
    }
}

// Module for camera clips
module camera_clips() {
    color(color_mount)
    union() {
        // Front clips
        for(side = [-1, 1]) {
            translate([side*(camera_w/2+wall_th/2), 0, base_d])
                cube([wall_th, camera_h+wall_th*2, clip_h]);

            // Clip cutouts
            translate([side*(camera_w/2+wall_th/2), 0, base_d+clip_h-clip_gap])
                cube([wall_th+0.5, camera_h+wall_th*2, clip_gap]);
        }

        // Side clips
        for(side = [-1, 1]) {
            translate([0, side*(camera_h/2+wall_th/2), base_d])
                cube([camera_w+wall_th*2, wall_th, clip_h]);

            // Clip cutouts
            translate([0, side*(camera_h/2+wall_th/2), base_d+clip_h-clip_gap])
                cube([camera_w+wall_th*2, wall_th+0.5, clip_gap]);
        }
    }
}

