connector_width = 8.6;
connector_height = 2.4;
insert_depth = 6;

// Strain relief parameters
strain_length = 10;
strain_diameter = 8;

// Main cable assembly
module usb_c_cable() {
    // Left USB-C connector
    translate([0, 0, 0])
        usb_c_connector();

    // Left strain relief
    translate([connector_length, 0, 0])
        rotate([0, 90, 0])
            strain_relief();

    // Wire between strain reliefs
    translate([connector_length + strain_length, 0, 0])
        rotate([0, 90, 0])
            cable_wire(wire_length);

    // Right strain relief
    translate([connector_length + strain_length + wire_length, 0, 0])
        rotate([0, 90, 0])
            strain_relief();

    // Right USB-C connector
    translate([connector_length + strain_length + wire_length + strain_length, 0, 0])
        usb_c_connector();
}

// USB-C connector module
module usb_c_connector() {
    difference() {
        // Outer metal shell
        color(color_shield)
            cube([connector_length, connector_width, connector_height], center = true);

        // Inner cavity for insert
        translate([0, 0, -0.8])
            cube([connector_length + 1, connector_width - 1.2, connector_height - 1], center = true);

        // USB-C insert opening
        translate([connector_length/2 - insert_depth/2, 0, 0])
            cube([insert_depth + 0.1, 6.7, 2.2], center = true);
    }

    // Internal insert
    translate([connector_length/2 - insert_depth/2, 0, 0])
        color(color_insert)
            cube([insert_depth, 6.5, 2], center = true);

    // Connector pins (simplified)
    for (i = [-3:3]) {
        translate([connector_length/2 - insert_depth + 1, i * 0.65, 0])
            color("gold")
                cube([0.3, 0.3, 1.8], center = true);
    }

    // Logo on top
    translate([0, 0, connector_height/2 + 0.1])
        color(color_logo)
            cube([3, 1.5, 0.1], center = true);
}

// Strain relief module
module strain_relief() {
    // Flexible boot
    color(color_body)
        cylinder(h = strain_length, d1 = strain_diameter, d2 = wire_diameter, center = false, $fn = 20);

    // Reinforcement rings
    for (i = [0:2]) {
        translate([0, 0, i * strain_length/3])
            color(color_body)
                cylinder(h = 1, d = strain_diameter + 0.5, center = false, $fn = 20);
    }
}

// Wire module
module cable_wire(length) {
    // Outer insulation
    color(color_wire)
        cylinder(h = length, d = wire_diameter, center = false, $fn = 20);

    // Internal wires (visible in cross-section)
    for (i = [0:3]) {
        rotate([0, 0, i * 90])
            translate([wire_diameter/4, 0, length/2])
                color("red")
                    cylinder(h = length, d = 1, center = true, $fn = 8);
    }
}

