base_height = 15;
usb_port_depth = 8;
usb_port_width = 12;
button_diameter = 6;
led_diameter = 3;

// Colors
body_color = [0.15, 0.15, 0.15];  // Dark gray
blade_color = [0.8, 0.8, 0.8];    // Light gray
guard_color = [0.5, 0.5, 0.5];    // Mid gray
accent_color = [0.9, 0.1, 0.1];   // Red accent

// Main fan assembly
module fan_assembly() {
    // Front guard
    color(guard_color)
    translate([0, 0, fan_depth/2 + guard_thickness/2])
        fan_guard();

    // Rear guard
    color(guard_color)
    translate([0, 0, -fan_depth/2 - guard_thickness/2])
        fan_guard();

    // Fan body/housing
    color(body_color)
    difference() {
        cylinder(h = fan_depth, d = fan_diameter, $fn = 64);

        // Hollow center for motor
        translate([0, 0, -1])
            cylinder(h = fan_depth + 2, d = 25, $fn = 32);
    }

    // Fan blades
    color(blade_color)
    translate([0, 0, -2])
        fan_blades();

    // Motor hub
    color(body_color)
    translate([0, 0, -4])
        cylinder(h = 8, d = 15, $fn = 32);

    // Base/stand
    color(body_color)
    translate([0, 0, -fan_depth/2 - base_height])
        base();
}

// Fan guard with concentric rings
module fan_guard() {
    difference() {
        cylinder(h = guard_thickness, d = fan_diameter, $fn = 64);

        // Remove center hole
        translate([0, 0, -1])
            cylinder(h = guard_thickness + 2, d = 20, $fn = 32);
    }

    // Radial spokes
    for(i = [0:11]) {
        rotate([0, 0, i * 30])
            translate([fan_diameter/4, -guard_thickness/2, 0])
                cube([fan_diameter/2, guard_thickness, guard_thickness]);
    }
}

// Fan blades with realistic twist
module fan_blades() {
    for(i = [0:blade_count-1]) {
        rotate([0, 0, i * (360/blade_count)])
            translate([0, 12, 0])
                rotate([0, 15, 0])
                    scale([1, 0.8, 0.2])
                        cylinder(h = 25, d1 = 15, d2 = 8, $fn = 3);
    }
}

// Base with electronics
module base() {
    difference() {
        // Main base
        cube([base_width, base_width, base_height], center = true);

        // USB port
        translate([0, -base_width/2 - 1, 3])
