// Create rounded rectangle module
module rounded_rect(width, height, depth, radius) {
    hull() {
        translate([radius, radius, 0]) cylinder(h=depth, r=radius, $fn=20);
        translate([width-radius, radius, 0]) cylinder(h=depth, r=radius, $fn=20);
        translate([width-radius, height-radius, 0]) cylinder(h=depth, r=radius, $fn=20);
        translate([radius, height-radius, 0]) cylinder(h=depth, r=radius, $fn=20);
    }
}

// Create gel layer module
module gel_layer(width, height, thickness) {
    color(gel_color) {
        difference() {
            rounded_rect(width, height, thickness, corner_radius);
            translate([gel_thickness, gel_thickness, -0.1]) 
                rounded_rect(width-2*gel_thickness, height-2*gel_thickness, thickness+0.2, corner_radius-1);
        }
    }
}

// Create screen module
module screen() {
    screen_width = 45;
    screen_height = 35;
    screen_depth = 1;

    translate([device_width/2 - screen_width/2, device_height*0.7 - screen_height/2, device_thickness+gel_thickness-0.1]) {
        color(screen_color) cube([screen_width, screen_height, screen_depth]);

        // Screen details
        for (x = [2:8:screen_width-6]) {
            for (y = [2:8:screen_height-6]) {
                translate([x, y, 0.5]) color([0.2, 0.2, 0.3]) cube([6, 6, 0.1]);
            }
        }
    }
}

// Create button module
module button(x, y, symbol) {
    button_size = 8;
    button_height = 1.5;

    translate([x, y, device_thickness+gel_thickness]) {
        color(button_color) {
            difference() {
                cylinder(h=button_height, r=button_size/2, $fn=16);
                translate([0, 0, -0.1]) cylinder(h=button_height+0.2, r=button_size/2-1.5, $fn=16);
            }
        }

        // Symbol on button
        if (symbol == "power") {
            translate([0, 0, button_height+0.1]) {
                color([0.8, 0.8, 0.8]) {
                    difference() {
                        cylinder(h=0.2, r=button_size/3, $fn=32);
                        translate([0, 0, -0.1]) cylinder(h=0.4, r=button_size/3-1, $fn=32);
                    }
                }
            }
        }
        if (symbol == "plus") {
            translate([0, 0, button_height+0.1]) {
                color([0.8, 0.8, 0.8]) {
                    cube([6, 1.5, 0.2], center=true);
                    cube([1.5, 6, 0.2], center=true);
                }
            }
        }
    }
}

