// Throttle controller for flight simulators
// Realistic style, medium complexity

$fn = 50;

// Main dimensions
base_width = 80;
base_depth = 60;
base_height = 12;
lever_height = 70;
lever_diameter = 12;
screen_width = 40;
screen_height = 20;
screen_depth = 3;

// Colors
color_base = [0.2, 0.2, 0.2];
color_lever = [0.1, 0.1, 0.1];
color_buttons = [0.8, 0.8, 0.8];
color_screen = [0.1, 0.1, 0.1];
color_screen_active = [0, 0.8, 0];

module base_plate() {
    color(color_base)
    difference() {
        // Main base
        translate([0, 0, base_height/2])
            cube([base_width, base_depth, base_height], center=true);

        // Cable channel
        translate([-base_width/2 - 5, 0, base_height/2])
            rotate([0, 90, 0])
                cylinder(h=10, r=5, center=true);

        // Mounting holes
        for(x = [-30, 30])
            for(y = [-20, 20])
                translate([x, y, base_height/2])
                    cylinder(h=base_height+1, r=2, center=true);
    }
}

module lever_assembly() {
    // Lever base
    color(color_base)
    translate([0, 0, base_height])
        cylinder(h=8, r=15, center=false);

    // Lever shaft
    color(color_lever)
    translate([0, 0, base_height + 4])
        rotate([0, 0, 15])
            cylinder(h=lever_height, r=lever_diameter/2, center=false);

    // Lever grip
    color(color_lever)
    translate([0, 0, base_height + lever_height + 4])
        rotate([0, 0, 15])
            difference() {
                cylinder(h=25, r=lever_diameter, center=false);
                // Finger grooves
                for(i = [0:3]) {
                    translate([0, -lever_diameter + 2 + i*5, 15])
                        rotate([90, 0, 0])
                            cylinder(h=20, r=2, center=true);
                }
            }

    // Detents
    color(color_base)
    for(angle = [-30:15:30]) {
        rotate([0, 0, angle])
            translate([0, 12, base_height + 4])
                cube([1, 3, 2], center=true);
    }
}

module display_screen() {
    // Screen housing
    color(color_base)
    translate([base_width/2 - 25, 0, base_height + 15])
        cube([screen_width + 6, screen_depth + 6, screen_height + 6], center=true);

    // Screen
    color(color_screen)
    translate([base_width/2 - 25, screen_depth/2 + 1, base_height + 15])
        cube([screen_width, 1, screen_height], center=true);

    // Screen content
    color(color_screen_active)
    translate([base_width/2 - 25, screen_depth/2 + 2, base_height + 15])
        cube([screen_width - 4, 0.5, screen_height - 4], center=true);

    // Screen details
    for(i = [0:2]) {
        color(color_screen_active)
        translate([base_width/2 - 35 + i*10, screen_depth/2 + 2.1, base_height + 10 + i*5])
            cube([2, 0.5, 8], center=true);
    }
}

module buttons() {
    // Top buttons
    for(i = [0:3]) {
        color(color_buttons)
        translate([-20 + i*15, base_depth/2 - 8, base_height + 20])
            difference() {
                cylinder(h=3, r=5, center=false);
                translate([0, 0, -0.5])
                    cylinder(h=4, r=3, center=false);
            }
    }

    // Side buttons
    for(i = [0:1]) {
        color(color_buttons)
        translate([base_width/2 - 8, -15 + i*30, base_height + 8])
            difference() {
                cube([6, 10, 4], center=true);
                translate([0, 0, -0.5])
                    cube([4, 8, 5], center=true);
            }
    }
}

module usb_port() {
    color(color_base)
    translate([-base_width/2 + 2, 0, base_height/2])
        cube([8, 12, 6], center=true);

    color([0.1, 0.1, 0.1])
    translate([-base_width/2 + 1, 0, base_height/2])
        cube([2, 8, 4], center=true);
}

// Assembly
base_plate();
lever_assembly();
display_screen();
buttons();
usb_port();

// LED indicators
for(i = [0:2]) {
    color([1, 0, 0])
    translate([10 + i*8, -base_depth/2 + 5, base_height + 1])
        cylinder(h=1, r=2, center=false);
}

