// Realistic Rifle-Mounted Electronic Device (Medium Complexity)
// A tactical laser rangefinder module that attaches to rifle rails

// Global settings
$fn = 32;

// Main colors
color_body = [0.2, 0.2, 0.2];      // Dark gray
color_screen = [0.1, 0.1, 0.1];    // Black
color_buttons = [0.8, 0.8, 0.8];   // Light gray
color_laser = [0.9, 0.1, 0.1];     // Red
color_mount = [0.3, 0.3, 0.3];     // Medium gray

// Main dimensions
device_length = 45;
device_width = 25;
device_height = 20;

// Screen parameters
screen_width = 18;
screen_height = 12;
screen_depth = 1;

// Button parameters
button_size = 3;
button_height = 1.5;
num_buttons = 3;

// Rail mount parameters
rail_width = 20;
rail_height = 8;
rail_length = 30;

// Laser emitter parameters
laser_diameter = 6;
laser_length = 8;

// Main device body
module device_body() {
    color(color_body)
    difference() {
        // Main box
        translate([-device_length/2, -device_width/2, -device_height/2])
            cube([device_length, device_width, device_height]);

        // Screen recess
        translate([-device_length/2 + 5, -screen_width/2, device_height/2 - screen_depth])
            cube([screen_depth + 1, screen_width, screen_height]);

        // Button holes
        for(i = [0:num_buttons-1]) {
            translate([device_length/2 - 8, -device_width/2 + 5 + i*7, -device_height/2])
                cube([button_size, button_size, button_height + 1]);
        }

        // USB port
        translate([device_length/2 - 3, -device_width/2 - 0.5, -5])
            cube([6, 2, 4]);
    }
}

// OLED display screen
module screen() {
    color(color_screen)
    translate([-device_length/2 + 4.5, -screen_width/2 + 1, device_height/2 - screen_depth/2])
        cube([screen_depth, screen_width - 2, screen_height - 2]);
}

// Control buttons
module buttons() {
    for(i = [0:num_buttons-1]) {
        color(color_buttons)
        translate([device_length/2 - 8 + button_size/2, -device_width/2 + 5 + i*7 + button_size/2, -device_height/2 + button_height/2])
            cube([button_size - 0.5, button_size - 0.5, button_height], center=true);
    }
}

// Laser emitter module
module laser_emitter() {
    color(color_body)
    union() {
        // Emitter housing
        translate([-device_length/2 - laser_length, -laser_diameter/2, -laser_diameter/2])
            cylinder(h=laser_length, d=laser_diameter);

        // Laser aperture
        color(color_laser)
        translate([-device_length/2 - laser_length - 0.5, 0, 0])
            cylinder(h=1, d=laser_diameter/2);
    }
}

// Picatinny rail mount
module rail_mount() {
    color(color_mount)
    difference() {
        // Main mount block
        translate([-rail_length/2, -rail_width/2, -device_height/2 - rail_height])
            cube([rail_length, rail_width, rail_height]);

        // Rail slots
        for(i = [-2:2]) {
            translate([i*5, -rail_width/2 - 0.5, -device_height/2 - rail_height + 3])
                cube([3, rail_width + 1, 2]);
        }

        // Attachment screw holes
        translate([-rail_length/2 + 5, 0, -device_height/2 - rail_height/2])
            rotate([90, 0, 0])
                cylinder(h=rail_height, d=3);

        translate([rail_length/2 - 5, 0, -device_height/2 - rail_height/2])
            rotate([90, 0, 0])
                cylinder(h=rail_height, d=3);
    }
}

// Battery compartment cover
module battery_cover() {
    color(color_body)
    difference() {
        translate([0, device_width/2 - 2, -device_height/2 + 3])
            cube([device_length - 10, 4, 6]);

        // Grip texture
        for(i = [-2:2]) {
            translate([i*4 - 8, device_width/2 - 1, -device_height/2 + 3.5])
                cube([2, 0.5, 5]);
        }

        // Thumb screw
        translate([0, device_width/2 + 2, -device_height/2 + 6])
            cylinder(h=3, d=4, $fn=6);
    }
}

// LED indicator lights
module indicators() {
    // Power LED
    color([0, 1, 0])
    translate([device_length/2 - 12, -device_width/2 - 0.5, 3])
        cylinder(h=1, d=2);

    // Low battery LED
    color([1, 0.5, 0])
    translate([device_length/2 - 12, -device_width/2 - 0.5, 0])
        cylinder(h=1, d=2);
}

