// Rounded cube helper
module rounded_cube(size, radius) {
    hull() {
        for (x = [0, size[0]])
        for (y = [0, size[1]])
        for (z = [0, size[2]]) {
            translate([x, y, z])
            sphere(r = radius);
        }
    }
}

