// Haptic Electronic Device - Medium Complexity Realistic Style
// 3D Printable Haptic Feedback Controller

$fn = 32;

// Main dimensions
base_width = 65;
base_height = 25;
base_depth = 45;
grip_radius = 12;
screen_width = 35;
screen_height = 20;
button_diameter = 8;
motor_housing_radius = 15;

// Colors
color_body = [0.15, 0.15, 0.15];  // Dark gray
color_screen = [0.1, 0.1, 0.1];   // Black
color_accent = [0.8, 0.2, 0.2];   // Red accent
color_metal = [0.7, 0.7, 0.7];    // Silver metal
color_buttons = [0.2, 0.2, 0.2];  // Dark buttons

// Main body assembly
module haptic_device() {
    union() {
        // Main body shell
        color(color_body)
        difference() {
            // Outer shell
            rounded_box(base_width, base_height, base_depth, 3);

            // Hollow interior
            translate([0, -1, 0])
            rounded_box(base_width - 4, base_height - 2, base_depth - 4, 2);
        }

        // Left grip
        translate([-base_width/2 + 5, 0, base_depth/2 - 10])
        rotate([90, 0, 0])
        grip();

        // Right grip
        translate([base_width/2 - 5, 0, base_depth/2 - 10])
        rotate([90, 0, 0])
        grip();

        // Screen assembly
        translate([0, base_height/2 - 2, 0])
        screen_assembly();

        // Button cluster
        translate([0, -base_height/2 + 5, base_depth/2 - 8])
        button_cluster();

        // Haptic motor housings
        translate([-base_width/2 + 10, 0, -base_depth/2 + 10])
        motor_housing();

        translate([base_width/2 - 10, 0, -base_depth/2 + 10])
        motor_housing();

        // USB-C port
        translate([0, -base_height/2, -base_depth/2 + 3])
        usb_port();

        // Status LEDs
        translate([-15, -base_height/2 - 0.1, 10])
        led_indicator();

        translate([15, -base_height/2 - 0.1, 10])
        led_indicator();
    }
}

// Rounded box helper
module rounded_box(w, h, d, r) {
    hull() {
        for(x = [-1, 1])
        for(y = [-1, 1])
        for(z = [-1, 1])
        translate([x*(w/2-r), y*(h/2-r), z*(d/2-r)])
        sphere(r=r);
    }
}

// Grip module
module grip() {
    color(color_body)
    difference() {
        cylinder(h = 25, r1 = grip_radius, r2 = grip_radius * 0.9);
        cylinder(h = 26, r1 = grip_radius - 3, r2 = grip_radius * 0.9 - 3);
    }

    // Grip texture rings
    for(i = [5:5:20]) {
        translate([0, 0, i])
        color([0.1, 0.1, 0.1])
        cylinder(h = 1, r = grip_radius + 0.5);
    }
}

// Screen assembly
module screen_assembly() {
    // Screen bezel
    color(color_body)
    translate([0, 0, -2])
    cube([screen_width + 4, 3, screen_height + 4], center = true);

    // Screen
    color(color_screen)
    translate([0, 1, 0])
    cube([screen_width, 0.5, screen_height], center = true);

    // Screen protector
    color([0.9, 0.9, 0.9, 0.3])
    translate([0, 2, 0])
    cube([screen_width + 2, 0.2, screen_height + 2], center = true);
}

// Button cluster
module button_cluster() {
    // Button grid 2x2
    for(i = [0, 1])
    for(j = [0, 1]) {
        x = (i - 0.5) * 12;
        z = (j - 0.5) * 12;

        // Button base
        color(color_body)
        translate([x, -1, z])
        cylinder(h = 2, r = button_diameter/2 + 1);

        // Actual button
        color(color_buttons)
        translate([x, 0, z])
        cylinder(h = 1.5, r = button_diameter/2);

        // Button symbol
        color([0.9, 0.9, 0.9])
        translate([x, 1.6, z])
        sphere(r = 0.5);
    }
}

// Haptic motor housing
module motor_housing() {
    color(color_metal)
    difference() {
        cylinder(h = 8, r = motor_housing_radius);
        translate([0, -1, 0])
        cylinder(h = 10, r = motor_housing_radius - 2);
    }

    // Motor symbol
    color(color_accent)
    translate([0, 4, 0])
    rotate([90, 0, 0])
    cylinder(h = 0.5, r = 8);
}

// USB-C port
module usb_port() {
    color(color_metal)
    difference() {
        translate([0, -1, 0])
        cube([8, 2, 4], center = true);
        translate([0, -2, 0])
        cube([6, 2, 2.5], center = true);
    }
}

// LED indicator
module led_indicator() {
    color([0.1, 0.1, 0.1])
    cylinder(h = 0.5, r = 2);
    translate([0, 0.6, 0])
    color([0, 1, 0, 0.8])
    sphere(r = 1.5);
}

// Render the complete device
haptic_device();

