    color([0.1, 0.1, 0.1]) difference() {
        // Main case
        translate([0, 0, watch_thickness/2]) {
            cube([watch_width, watch_height, watch_thickness], center=true);
        }

        // Top chamfer
        translate([0, 0, watch_thickness - 0.5]) {
            cube([watch_width + 2, watch_height + 2, 1], center=true);
        }

        // Screen cutout
        translate([0, 0, watch_thickness/2 + 0.1]) {
            cube([watch_width - screen_border*2, watch_height - screen_border*2, watch_thickness], center=true);
        }
    }
}

// Screen
module screen() {
    color([0, 0, 0]) translate([0, 0, watch_thickness/2 + 0.2]) {
        cube([watch_width - screen_border*2 - 2, watch_height - screen_border*2 - 6, 0.5], center=true);
    }

    // Screen content - simple clock face
    color([0.2, 0.8, 0.2]) translate([0, 0, watch_thickness/2 + 0.5]) {
        cube([watch_width - screen_border*2 - 4, watch_height - screen_border*2 - 8, 0.1], center=true);
