    color([0.3, 0.3, 0.3]) translate([watch_width/2 + button_length/2, 0, watch_thickness/2]) {
        rotate([0, 90, 0]) {
            cylinder(h=button_length, d=button_diameter, center=true);
            translate([0, 0, button_length/2]) {
                sphere(d=button_diameter);
            }
        }
