module button(is_jump=false) {
    difference() {
        // Button housing
        color([0.8, 0.8, 0.8])
            cylinder(h=button_height, r=button_diameter/2);

        // Keycap mount
        translate([0, 0, -1])
            cylinder(h=button_height+2, r=3);
    }

    // Keycap
    color(is_jump ? [0.2, 0.2, 0.8] : [0.9, 0.9, 0.9])
