        button();
    translate([0, button_spacing, base_height])
        button();

    // Action buttons (right side)
    translate([button_spacing*2, button_spacing/2, base_height])
        button();
    translate([button_spacing*3, 0, base_height])
        button();
    translate([button_spacing*2, -button_spacing/2, base_height])
        button();
    translate([button_spacing*3, button_spacing, base_height])
