// Display bezel & screen
// -----------------------------
module display() {
    // bezel
    color("black")
    translate([10, 6, 18])
        roundedCube([40, 28, 2], 2);

    // LCD
    color("royalblue")
    translate([12, 8, 20.1])
        cube([36, 24, 0.5]);
}

// -----------------------------
// Buttons (menu / OK / + / â€“)
// -----------------------------
module buttons() {
    for (i = [0:3]) {
        translate([5 + i * 12, 36, 9])
            color("red")
            cylinder(h = 3, r = 3, $fn = 20);
    }
}

// -----------------------------
// USB-C port
// -----------------------------
module usbPort() {
    color("dimgray")
    translate([0, 20, 9])
        rotate([0, 90, 0])
            cube([8, 7, 3], center = true);
}

// -----------------------------
// LED indicators
