// Realistic Flight Electronic Device - GPS Navigator
// Dimensions: 75mm x 50mm x 18mm

$fn = 32;

// Main colors
color_case = [0.15, 0.15, 0.15];
color_screen = [0.1, 0.1, 0.1];
color_buttons = [0.3, 0.3, 0.3];
color_antenna = [0.2, 0.2, 0.2];
color_led = [0, 1, 0];

// Main body
module main_body() {
    color(color_case) {
        // Main enclosure
        difference() {
            rounded_cube([75, 50, 15], 3);

            // Screen cutout
            translate([5, 10, 14])
                cube([50, 30, 2]);

            // Button cutouts
            for(i = [0:3]) {
                translate([60, 10 + i*10, 14])
                    cylinder(h=2, r=3);
            }

            // USB port
            translate([2, 20, 7])
                cube([4, 8, 3]);
        }

        // Side grips
        for(side = [-1, 1]) {
            translate([0, side*20, 0])
                cube([75, 10, 15], center=true);
        }
    }
}

// Rounded cube helper
module rounded_cube(size, radius) {
    hull() {
        for(x = [0, 1], y = [0, 1], z = [0, 1]) {
            translate([
                x * size[0] - radius * (x == 0),
                y * size[1] - radius * (y == 0),
                z * size[2] - radius * (z == 0)
            ]) {
                sphere(r=radius);
            }
        }
    }
}

// Screen assembly
module screen() {
    // LCD display
    color(color_screen) {
        translate([5, 10, 13])
            cube([50, 30, 1]);
    }

    // Screen bezel
    difference() {
        color([0.05, 0.05, 0.05]) {
            translate([4, 9, 12.5])
                cube([52, 32, 0.5]);
        }

        translate([5.5, 10.5, 12])
            cube([49, 29, 1]);
    }
}

// Buttons
module buttons() {
    for(i = [0:3]) {
        color(color_buttons) {
            translate([60, 10 + i*10, 12])
                cylinder(h=3, r=2.5);
        }

        // Button labels
        color([1, 1, 1]) {
            translate([60, 10 + i*10, 15.1])
                rotate([180, 0, 0])
                linear_extrude(0.1)
                text(str(chr(65+i)), size=3, halign="center");
        }
    }
}

// GPS antenna
module antenna() {
    color(color_antenna) {
        // Antenna base
        translate([37.5, 5, 15])
            cylinder(h=2, r=8);

        // Antenna dome
        translate([37.5, 5, 17])
            sphere(r=6);

        // Antenna pattern
        color([0.3, 0.3, 0.3]) {
            for(angle = [0:30:330]) {
                rotate([0, 0, angle])
                translate([37.5, 5, 17])
                    cube([0.5, 5, 0.5], center=true);
            }
        }
    }
}

// Status LED
module status_led() {
    color(color_led) {
        translate([70, 45, 13])
            cylinder(h=1, r=2);
    }

    // LED housing
    color([0.2, 0.2, 0.2]) {
        translate([70, 45, 12.5])
            cylinder(h=1.5, r=3);
    }
}

// USB port detail
module usb_port() {
    color([0.1, 0.1, 0.1]) {
        translate([0, 24, 7])
            cube([3, 4, 2]);
    }

    // USB contacts
    color([0.8, 0.8, 0.8]) {
        for(i = [0:3]) {
            translate([0.5, 22.5 + i*1, 7.5])
                cube([2, 0.5, 1]);
        }
    }
}

// Mounting points
module mounting_points() {
