// SIM card electronic device module
// Realistic style, medium complexity

// Main parameters
sim_width = 15;
sim_height = 12;
sim_thickness = 0.76;
corner_radius = 1.5;
edge_chamfer = 0.3;

// Colors
gold_color = [0.8, 0.6, 0.1];
plastic_color = [0.9, 0.9, 0.9];
contact_color = [0.7, 0.7, 0.7];

// SIM card body with rounded corners
module sim_body() {
    color(plastic_color) {
        difference() {
            // Main body
            minkowski() {
                cube([sim_width, sim_height, sim_thickness], center=true);
                cylinder(r=corner_radius, h=0.1, $fn=8);
            }

            // Chamfer edges
            translate([0, 0, sim_thickness/2 - 0.1]) {
                minkowski() {
                    cube([sim_width + 0.2, sim_height + 0.2, sim_thickness + 0.2], center=true);
                    cylinder(r=corner_radius + edge_chamfer, h=0.1, $fn=8);
                }
            }
        }
    }
}

// Contact pad array
module contact_pads() {
    color(contact_color) {
        for (i = [0:5]) {
            translate([-sim_width/2 + 2 + i*2.2, -sim_height/2 + 2.5, sim_thickness/2 - 0.01]) {
                cube([1.8, 7, 0.02], center=false);
            }
        }

        // Additional contact areas
        translate([sim_width/2 - 3.5, sim_height/2 - 3, sim_thickness/2 - 0.01]) {
            cube([2.5, 2, 0.02], center=false);
        }
    }
}

// Gold plating layer
module gold_plating() {
    color(gold_color) {
        translate([0, 0, -sim_thickness/2 + 0.01]) {
            cube([sim_width - 0.5, sim_height - 0.5, 0.02], center=true);
        }
    }
}

// Notch indicator
module notch_indicator() {
    color(plastic_color) {
        translate([sim_width/2 - 2.5, -sim_height/2 + 1, 0]) {
            difference() {
                cylinder(h=sim_thickness, r=1, $fn=16);
                translate([0.5, 0, -0.1]) {
                    cube([2, 2, sim_thickness + 0.2], center=true);
                }
            }
        }
    }
}

// Beveled edge detail
module beveled_edges() {
    color([0.8, 0.8, 0.8]) {
        difference() {
            cube([sim_width + 0.1, sim_height + 0.1, sim_thickness + 0.1], center=true);
            cube([sim_width - 0.2, sim_height - 0.2, sim_thickness + 0.2], center=true);
        }
    }
}

// SIM carrier tray (3D printable support)
module sim_tray() {
    tray_thickness = 1;
    tray_margin = 2;

    color([0.7, 0.7, 0.7]) {
        difference() {
            // Main tray
            translate([0, 0, -sim_thickness/2 - tray_thickness/2]) {
                cube([sim_width + tray_margin*2, sim_height + tray_margin*2, tray_thickness], center=true);
            }

            // SIM cutout
            translate([0, 0, -sim_thickness/2 - 0.1]) {
                minkowski() {
                    cube([sim_width + 0.2, sim_height + 0.2, 0.1], center=true);
                    cylinder(r=corner_radius, h=0.1, $fn=8);
                }
            }

            // Finger grip
            translate([sim_width/2 + tray_margin/2, 0, -sim_thickness/2 - tray_thickness/2]) {
                cylinder(h=tray_thickness, r=2, $fn=16);
            }
        }
    }
}

