// Capture Device - Medium Complexity Realistic Style
// OpenSCAD 3D Printable Model

// Main parameters
device_width = 48;
device_height = 30;
device_depth = 72;
wall_thickness = 2;

// Lens parameters
lens_diameter = 18;
lens_depth = 8;
lens_protrusion = 4;

// Screen parameters
screen_width = 25;
screen_height = 15;
screen_depth = 1;

// Button parameters
button_size = 4;
button_height = 1.5;

// Colors
color_body = [0.15, 0.15, 0.15];
color_lens = [0.1, 0.1, 0.1];
color_screen = [0, 0, 0];
color_button = [0.8, 0.8, 0.8];
color_port = [0.3, 0.3, 0.3];

// Main body
module main_body() {
    color(color_body)
    difference() {
        // Main box
        cube([device_width, device_depth, device_height], center=true);

        // Lens hole front
        translate([0, device_depth/2 - lens_depth/2, 0])
        rotate([90, 0, 0])
        cylinder(h=lens_depth*2, d=lens_diameter + wall_thickness, center=true, $fn=50);

        // Screen recess
        translate([0, -device_depth/2 + screen_depth + 2, device_height/2 - screen_height/2 - 5])
        cube([screen_width + 2, screen_depth + 1, screen_height + 2], center=true);

        // Microphone holes top
        for(i = [-1:1]) {
            translate([i * 8, 0, device_height/2 - 3])
            cylinder(h=wall_thickness + 1, d=2, center=true, $fn=20);
        }

        // Speaker holes
        for(x = [-2:2]) {
            for(y = [-1:1]) {
                translate([x * 3.5, -device_depth/2 + 5, y * 3.5 - 5])
                cylinder(h=wall_thickness + 1, d=1.2, center=true, $fn=8);
            }
        }

        // Status LED hole
        translate([device_width/2 - 5, device_depth/2 - 4, device_height/2 - 5])
        cylinder(h=wall_thickness + 1, d=3, center=true, $fn=20);
    }

    // Grip texture sides
    color([0.2, 0.2, 0.2])
    for(i = [0:3]) {
        translate([device_width/2 - wall_thickness/2, -device_depth/2 + i * 18 + 5, 0])
        cube([wall_thickness, 2, device_height - 8], center=true);
    }

    mirror([1, 0, 0])
    for(i = [0:3]) {
        translate([device_width/2 - wall_thickness/2, -device_depth/2 + i * 18 + 5, 0])
        cube([wall_thickness, 2, device_height - 8], center=true);
    }
}

// Lens assembly
module lens_assembly() {
    color(color_lens)
    union() {
        // Lens barrel
        translate([0, device_depth/2 + lens_protrusion/2, 0])
        rotate([90, 0, 0])
        cylinder(h=lens_protrusion, d=lens_diameter + 4, center=true, $fn=50);

        // Front lens element
        translate([0, device_depth/2 + lens_protrusion, 0])
        rotate([90, 0, 0])
        cylinder(h=1, d=lens_diameter, center=true, $fn=50);

        // Lens ring detail
        translate([0, device_depth/2 + lens_protrusion/2 - 1, 0])
        rotate([90, 0, 0])
        cylinder(h=2, d=lens_diameter + 6, center=true, $fn=100);
    }
}

// Screen assembly
module screen_assembly() {
    color(color_screen)
    translate([0, -device_depth/2 + screen_depth/2 + 2, device_height/2 - screen_height/2 - 5])
    cube([screen_width, screen_depth, screen_height], center=true);

    // Screen bezel
    color([0.25, 0.25, 0.25])
    translate([0, -device_depth/2 + screen_depth/2 + 1.8, device_height/2 - screen_height/2 - 5])
    cube([screen_width + 2, screen_depth + 0.5, screen_height + 2], center=true);
}

// Buttons
module buttons() {
    color(color_button)
    // Record button (red accent)
    translate([0, -device_depth/2 + 8, -device_height/2 + 8])
    cube([button_size, button_height, button_size], center=true);

    color([0.8, 0.1, 0.1])
    translate([0, -device_depth/2 + 8.5, -device_height/2 + 8])
    cube([button_size - 1, button_height + 0.5, button_size - 1], center=true);

    // Power button
    color(color_button)
    translate([-device_width/2 + 8, -device_depth/2 + 8, 0])
    cube([button_size, button_height, button_size], center=true);

    // Mode button
    translate([device_width/2 - 8, -device_depth/2 + 8, 0])
    cube([button_size, button_height, button_size], center=true);
}

// Ports
module ports() {
    color(color_port)
    // Micro USB port
    translate([device_width/2 - 8, 0, -device_height/2 + 3])
    cube([4, 7, 2], center=true);

    // HDMI port
    translate([device_width/2 - 8, 0, -device_height/2 + 8])
    cube([6, 9, 3], center=true);

    // SD card slot
    translate([0, device_depth/2 - 5, -device_height/2 + 3])
    cube([12, 1, 2], center=true);
}

// Mounting point
module mounting_point() {
    color([0.4, 0.4, 0.4])
    translate([0, -device_depth/2 + 15, -device_height/2 + 1])
    rotate([90, 0, 0])
    cylinder(h=4, d=6, center=true, $fn=20);

    // Thread hole
    translate([0, -device_depth/2 + 15, -device_height/2 - 1])
    rotate([90, 0, 0])
    cylinder(h=6, d=3, center=true, $fn=20);
}

// Status LED
module status_led() {
    color([0, 1, 0])
    translate([device_width/2 - 5, device_depth/2 - 4, device_height/2 - 5])
    cylinder(h=wall_thickness, d=2, center=true, $fn=20);
}

// Assemble the capture device
union() {
    main_body();
    lens_assembly();
    screen_assembly();
    buttons();
    ports();
    mounting_point();
    status_led();
}

