// Smart-Fitness Band (realistic medium-complexity arm electronic device)
// Overall printable size â‰ˆ 88 mm long Ã— 34 mm wide Ã— 18 mm high

$fn = 36;

// Main colours
bandColor   = "#1E1E1E";    // matte black TPU
bodyColor   = "#2C2C2E";    // dark aluminium
glassColor  = "#0B0B0B";    // smoked glass
accentColor = "#FF3B30";    // red accent ring

// ---------- Main assembly ----------
module smartBand() {
    difference() {
        union() {
            bandTop();    // upper strap
            bandBottom(); // lower strap
            body();       // watch body
            screen();     // display glass
            sideButton(); // side button & crown
            chargePins(); // underside charge contacts
        }
        // Remove strap holes for breathability
        strapHoles();
    }
}

// ---------- Soft TPU strap modules ----------
module strapSegment(w, l, t) {
    color(bandColor)
    translate([-w/2, -l/2, 0])
        cube([w, l, t], center = false);
}

module bandTop() {
    for (i = [-1:1]) {
        translate([0, 15 + i*10, -3])
            strapSegment(22 - abs(i)*3, 8, 4);
    }
    // Tapered end
    translate([0, 42, -3])
        scale([0.6, 1, 1])
            strapSegment(22, 10, 4);
}

module bandBottom() {
    mirror([0, 1, 0]) {
        bandTop();
    }
}

// ---------- Watch body ----------
module body() {
    color(bodyColor)
    translate([0, 0, 0]) {
        hull() {
            cylinder(h = 11, d = 34);
            translate([0, 0, 1.5])
                cylinder(h = 8, d = 32);
        }
    }
}

// ---------- Display ----------
module screen() {
    color(glassColor)
    translate([0, 0, 11.2])
        cylinder(h = 0.8, d = 28);

    // Accent ring beneath glass
    color(accentColor)
    translate([0, 0, 10.5])
        cylinder(h = 0.7, d1 = 28.2, d2 = 28);
}

// ---------- Side controls ----------
module sideButton() {
    // Main button
    color(bodyColor)
    translate([17, 0, 5])
        rotate([0, 90, 0])
            cylinder(h = 3, d = 5, center = true);
    // Crown
    color(accentColor)
    translate([19.5, 0, 5])
        rotate([0, 90, 0])
            cylinder(h = 2, d = 4);
}

// ---------- Underside charging contacts ----------
module chargePins() {
    for (i = [-1, 1]) {
        color([0.8, 0.8, 0.8]) // silver contact
        translate([i*5, -5, -1.6])
            cylinder(h = 0.4, d = 3);
    }
}

// ---------- Ventilation holes in strap ----------
module strapHoles() {
    for (i = [0:3]) {
        translate([0, 15 + i*8, -1])
            rotate([90, 0, 0])
                cylinder(h = 20, d = 2.5);
    }
    mirror([0, 1, 0]) {
        for (i = [0:3]) {
            translate([0, 15 + i*8, -1])
                rotate([90, 0, 0])
                    cylinder(h = 20, d = 2.5);
        }
    }
}

// Render the device
smartBand();

