// Wrist-mounted fitness tracker / smartwatch
// Realistic medium-complexity OpenSCAD model
// Dimensions: 46 mm wide Ã— 38 mm tall Ã— 12 mm thick
// Fits standard 22 mm spring-bar watch strap

// Overall color scheme
strapColor = [0.15,0.15,0.15];       // matte black TPU
caseColor  = [0.2,0.2,0.2];        // dark grey aluminum
screenColor= [0.05,0.05,0.05];     // deep black glass
buttonColor= [0.25,0.25,0.25];     // metallic grey
accentColor= [0.9,0.2,0.2];        // red sport accent

// General dimensions (mm)
module wristTracker() {
    // Watch case body
    difference() {
        // Main rounded case
        color(caseColor) {
            hull() {
                translate([-23,-19,0]) cylinder(h=12, d=8, $fn=20);
                translate([ 23,-19,0]) cylinder(h=12, d=8, $fn=20);
                translate([-23, 19,0]) cylinder(h=12, d=8, $fn=20);
                translate([ 23, 19,0]) cylinder(h=12, d=8, $fn=20);
            }
        }

        // Screen cavity
        translate([0,0,1]) {
            color(screenColor) cube([40,34,2], center=true);
        }

        // Heart-rate sensor window
        translate([0,0,-0.1]) cylinder(h=3, d=8, $fn=30);
    }

    // Raised bezel ring
    translate([0,0,10.5]) {
        color(caseColor) difference() {
            cylinder(h=2, d=50, $fn=60);
            cylinder(h=3, d=42, $fn=60);
        }
    }

    // 2.5D curved cover glass
    translate([0,0,11]) {
        color(screenColor) scale([1,1,0.5]) sphere(d=42, $fn=60);
    }

    // Side buttons (3 physical pushers)
    module button(posY, height) {
        translate([24.5, posY, height]) {
            color(buttonColor) {
                difference() {
                    cylinder(h=3, d=4, $fn=20);
                    translate([0,-2,0]) cube([6,4,6], center=true);
                }
            }
            translate([1.5,0,1.5]) color(accentColor) sphere(d=1.8, $fn=12);
        }
    }

    button(12, 8.5);
    button(0, 8.5);
    button(-12, 8.5);

    // Microphone pinhole
    translate([24.8, 17, 8]) rotate([90,0,0]) cylinder(h=1.5, d=1.2, $fn=8);

    // Charging contacts
    translate([-24.5, -5, 1]) color([0.8,0.8,0.8]) cube([1.5,3,1]);
    translate([-24.5,  5, 1]) color([0.8,0.8,0.8]) cube([1.5,3,1]);

    // Vibration motor port (small grille)
    for(i=[-1:1]) {
        translate([-4+i*4, 19.5, 5]) rotate([90,0,0]) 
            color([0.1,0.1,0.1]) cylinder(h=1, d=0.8, $fn=6);
    }
}

// Watch straps (quick-release spring-bar type)
module strap(isTop=true) {
    color(strapColor) {
        // Spring-bar lug
        translate([isTop?-27:27,0,6]) rotate([0,90,0]) {
            difference() {
                cylinder(h=22, d=3.2, $fn=20);
                translate([0,0,-1]) cylinder(h=24, d=2.2, $fn=20);
            }
        }

        // TPU strap body with buckle holes
        translate([isTop?-35:35,0,6]) rotate([0,90,0]) {
            hull() {
                cylinder(h=5, d=3, $fn=8);
                cylinder(h=70, d=3, $fn=8);
            }
        }

        // Buckle holes pattern
        for(i=[1:7]) {
            translate([isTop?-45-i*8:45+i*8,0,6]) rotate([0,90,0]) 
                cylinder(h=6, d=2, $fn=10);
        }

        // Keeper loop
        if(isTop) {
            translate([-75,0,6]) rotate([0,90,0]) {
                difference() {
                    cylinder(h=10, d=6, $fn=20);
                    cylinder(h=11, d=4, $fn=20);
                }
            }
        }
    }

    // Metal buckle on bottom strap
    if(!isTop) {
        translate([75,0,6]) rotate([0,90,0]) color([0.5,0.5,0.5]) {
            difference() {
                cube([10,16,3], center=true);
                cube([8,14,4], center=true);
            }
            translate([0,8,2]) rotate([90,0,0]) cylinder(h=3, d=3, $fn=12);
        }
    }
}

// Assemble the complete device
wristTracker();
strap(true);
strap(false);

