module bumper() {
    // ---------- Main shell ----------
    difference() {
        // Outer rounded block
        minkowski() {
            cube([70, 145, 9], center = true);
            sphere(d = 5);
        }

        // Hollow out interior
        minkowski() {
            cube([65, 140, 8], center = true);
            sphere(d = 4);
        }

        // ---------- Port and button cut-outs ----------
        // Lightning port
        translate([0, -72.5, 0])
            cube([12, 3, 4], center = true);

        // Speaker grilles
        for (x = [-11, -5.5, 0, 5.5, 11])
            translate([x, -72.5, 0])
                cube([1.5, 2, 3], center = true);

        // Silent-switch slot
        translate([-34, -72.5, 2])
            cube([4, 2, 1.5], center = true);

        // Volume buttons recess (left side)
        for (y = [40, 52])
            translate([-34, y, 0])
                cube([2, 7, 3], center = true);

        // Power button recess (right side)
        translate([34, 60, 0])
            cube([2, 7, 3], center = true);

        // Camera island clearance (top)
        translate([13, 67, 3])
            cube([14, 8, 3], center = true);
    }

    // ---------- Camera island ring ----------
    difference() {
        color("black")
        translate([13, 67, 3.5])
