module keycap_base() {
    // Main keycap body with tapered sides
    difference() {
        // Outer shell
        hull() {
            translate([0, 0, key_depth]) cube([key_top_width, key_top_height, 0.1], center=true);
            translate([0, 0, 0]) cube([key_width, key_height, 0.1], center=true);
        }

        // Hollow interior
        translate([0, 0, -wall_thickness/2]) cube([key_width-wall_thickness*2, key_height-wall_thickness*2, key_depth+wall_thickness], center=true);

        // Stem socket
        translate([0, 0, -stem_height/2]) cylinder(h=stem_height+1, d=stem_diameter+1, center=true);
        translate([0, 0, -stem_height/2]) cylinder(h=stem_height+1, d=stem_inner_diameter, center=true);

        // Cross mount
        for (i = [-1, 1]) {
            translate([i * 2.5, 0, -stem_height/2]) cube([1, 4.5, stem_height+1], center=true);
            translate([0, i * 2.5, -stem_height/2]) cube([4.5, 1, stem_height+1], center=true);
        }
    }
}

module display_cutout() {
    // Cutout for OLED display
    translate([0, display_offset, key_depth-display_depth])
        cube([display_width, display_height, display_depth+1], center=true);
}

module display() {
    // OLED display module
    color([0.1, 0.1, 0.1]) 
        translate([0, display_offset, key_depth-display_depth/2])
            cube([display_width*0.95, display_height*0.9, display_depth], center=true);

