    color([0.3, 0.3, 0.3])
        translate([key_top_width/2 - sensor_width/2 - 1, display_offset + display_height/2 + sensor_height/2 + 1, key_depth-sensor_depth/2])
            cube([sensor_width, sensor_height, sensor_depth], center=true);
