    color(color_body)
    difference() {
        // Main body
        cube([body_width, body_depth, body_height], center=true);

        // Front face chamfer
        translate([0, body_depth/2-5, 0])
            rotate([90,0,0])
                cylinder(h=body_width, d1=body_height, d2=body_height-4, center=true);

        // Back face chamfer
        translate([0, -body_depth/2+5, 0])
            rotate([-90,0,0])
                cylinder(h=body_width, d1=body_height, d2=body_height-4, center=true);
    }

    // Side grip lines
    for(i = [-1, 1]) {
        translate([i*body_width/2+0.1, 0, 0])
            color([0.15, 0.15, 0.15])
            for(y = [-20:10:20]) {
                translate([0, y, 0])
                    cube([0.5, 8, body_height-4], center=true);
            }
    }
}

module lens_assembly() {
    // Lens housing
    translate([0, body_depth/2-lens_protrusion/2, 0])
    rotate([0, 90, 0])
    difference() {
        // Outer cylinder
        color(color_body)
        cylinder(h=lens_protrusion+2, d=lens_diameter+8, center=true);

        // Lens opening
        color(color_lens)
        cylinder(h=lens_protrusion+6, d=lens_diameter, center=true);
    }

    // Focus ring
    translate([0, body_depth/2+2, 0])
    rotate([0, 90, 0])
    color(color_ring)
    cylinder(h=3, d=lens_diameter+12, center=true);

    // Lens glass
    translate([0, body_depth/2+2, 0])
    rotate([0, 90, 0])
    color([0.05, 0.05, 0.05, 0.7])
    cylinder(h=1, d=lens_diameter-2, center=true);
}

module status_indicators() {
    // Status LED
    translate([body_width/2-3, body_depth/2-5, 5])
        color(color_led)
        sphere(d=2);

    // Logo area
    translate([0, -body_depth/2+8, body_height/2-1])
        color([0.3, 0.3, 0.3])
        cube([15, 6, 0.5], center=true);
}

module usb_c_port() {
    // USB-C port on back
    translate([0, -body_depth/2, 0])
    color(color_usb)
    difference() {
        cube([10, 5, 4], center=true);
        translate([0, 2.6, 0])
            cube([8, 2, 2.5], center=true);
    }
}

module mounting_clip() {
    // Hinge base
    translate([0, 0, -body_height/2-5])
    color(color_body)
    cube([body_width-10, 10, 10], center=true);

    // Clip arms
    for(x = [-body_width/4, body_width/4]) {
        translate([x, 5, -body_height/2-mount_clip_height/2-5])
        color(color_body)
        difference() {
            cube([8, 10, mount_clip_height], center=true);
            translate([0, 5, 0])
                cube([6, 12, mount_clip_height-4], center=true);
        }
    }

    // Clip spring detail
    translate([0, 5, -body_height/2-5-mount_clip_height/2])
        color(color_ring)
        cylinder(h=2, d=3, center=true);
}

// Main assembly
streamcam_body();
lens_assembly();
status_indicators();
usb_c_port();
mounting_clip();

