// Mixed-Reality headset â€“ realistic, medium-complexity OpenSCAD model
// 3D-printable, ~60 mm front-to-back, 50 mm tall
// All overhangs â‰¤45Â°, wall thickness 1.5 mm

$fa = 1; $fs = 0.5;

// --------------------
// Main assembly
// --------------------
color("DimGray") headset();

module headset() {
    // Central visor shell
    visor();

    // Left & right temples with hinges
    temple("left");
    temple("right");

    // Top strap bridge
    strap_bridge();

    // Front sensor bar
    sensor_bar();

    // Soft face-gasket ring
    gasket();
}

// --------------------
// Visor body
// --------------------
module visor() {
    difference() {
        // Outer shell
        hull() {
            translate([0, 0, 15]) cube([60, 25, 25], center = true);
            translate([0, 0, 15]) scale([0.9, 0.9, 1]) sphere(d = 30);
        }

        // Internal cavity
        translate([0, 0, 15]) cube([55, 20, 23], center = true);

        // Eye openings
        for (x = [-14, 14])
            translate([x, 0, 12]) rotate([0, 30, 0]) cylinder(h = 30, d = 18, $fn = 20);
    }

    // Front screen glass
    translate([0, 0, 27]) color("SkyBlue", 0.4)
        cube([58, 23, 1.5], center = true);

    // Ring of status LEDs
    for (a = [0:30:330])
        translate([0, 0, 28])
            rotate([0, 0, a])
            translate([22, 0, 0]) color("Red") cylinder(h = 0.8, d = 2, $fn = 8);
}

// --------------------
// Temple arms
// --------------------
module temple(side) {
    angle = (side == "left") ? 90 : -90;
    mirror([side == "left" ? 0 : 1, 0, 0]) {
        // Hinge block
        translate([30, 0, 15]) rotate([0, 0, angle])
            cube([8, 6, 6], center = true);

        // Arm
        translate([30, 0, 15]) rotate([0, 10, angle]) {
            difference() {
                hull() {
                    translate([0, 0, 0]) cube([8, 6, 6], center = true);
                    translate([25, 0, 0]) cube([6, 4, 4], center = true);
                }
                // Wire channel
                translate([12.5, 0, 0]) cube([25, 3, 3], center = true);
            }

            // Speaker grill holes
            for (i = [5:5:20])
                translate([i, 0, 2.5]) rotate([90, 0, 0])
                    cylinder(h = 4, d = 1.2, $fn = 8);
        }
    }
}

// --------------------
// Top strap bridge
// --------------------
module strap_bridge() {
    color("Black") {
        hull() {
            translate([-25, 0, 40]) sphere(d = 6);
            translate([25, 0, 40]) sphere(d = 6);
        }
    }
}

// --------------------
// Sensor bar
// --------------------
module sensor_bar() {
    translate([0, 0, 40]) rotate([20, 0, 0])
        difference() {
            cube([40, 8, 4], center = true);
            // Camera windows
            for (x = [-12, 0, 12])
                translate([x, 0, 2])
                    cube([6, 6, 2], center = true);
        }
}

// --------------------
// Facial gasket
// --------------------
module gasket() {
    color("DarkGray", 0.6)
        difference() {
            translate([0, 0, 8]) rotate_extrude() {
                translate([20, 0, 0]) circle(d = 5);
                translate([25, 0, 0]) circle(d = 5);
            }
            // Eye cut-outs
            for (x = [-14, 14])
                translate([x, 0, 8]) rotate([0, 30, 0]) cylinder(h = 30, d = 20, $fn = 20);
        }
}

