// Nintendo Switch-inspired handheld game console (store display unit)
// Scale: ~80 mm long, 3D-printable, no overhangs >45Â°

$fn = 36;               // Smooth curves
wall = 1.2;             // 3D-print friendly wall thickness
unit = 10;              // Grid unit for easy scaling

module shell() {
    // Main body
    color("LightGray") difference() {
        hull() {
            translate([-unit*2, -unit*1.5, 0])
                cube([unit*4, unit*3, unit*0.8]);
            translate([-unit*1.8, -unit*1.3, unit*0.4])
                cube([unit*3.6, unit*2.6, unit*0.4]);
        }
        // Cartridge slot
        translate([unit*1.5, -unit*0.4, -0.1])
            cube([unit*0.4, unit*0.8, unit*1]);
    }
}

module screen() {
    // Display bezel
    color("Black") difference() {
        translate([-unit*1.6, -unit*1.2, unit*0.81])
            cube([unit*3.2, unit*2.4, wall]);
        // Actual screen
        translate([-unit*1.5, -unit*1.1, unit*0.82])
            cube([unit*3, unit*2.2, wall+0.01]);
    }
    // Screen surface
    color([0.05,0.05,0.05]) translate([-unit*1.5, -unit*1.1, unit*0.83])
        cube([unit*3, unit*2.2, 0.2]);
}

module joystick(x, y, side) {
    color("DimGray") {
        // Base
        translate([x, y, unit*0.82]) cylinder(h=unit*0.25, r=unit*0.35);
        // Stick
        color("Black") translate([x, y, unit*1.07]) cylinder(h=unit*0.3, r=unit*0.25);
        // Cap
        color(side) translate([x, y, unit*1.37]) cylinder(h=unit*0.1, r=unit*0.28);
    }
}

module buttons() {
